interface Library {
  id: number;
  name: string;
  address: string;
  distance: number;
  phone: string;
  hours: string;
  status: string;
  type?: string;
}

interface BookSearchSectionProps {
  libraries: Library[];
  onLibrariesUpdate: (libraries: Library[]) => void;
  selectedRegion: string;
  onRegionUpdate: (region: string) => void;
  externalSearchQuery?: string;
  onExternalSearchComplete?: () => void;
}

const BookSearchSection: React.FC<BookSearchSectionProps> = ({ 
  libraries, 
  onLibrariesUpdate, 
  selectedRegion, 
  onRegionUpdate,
  externalSearchQuery,
  onExternalSearchComplete
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  // Ïô∏Î∂Ä Í≤ÄÏÉâ ÏøºÎ¶¨ Ï≤òÎ¶¨
  useEffect(() => {
    if (externalSearchQuery) {
      setSearchTerm(externalSearchQuery);
      performSearch(externalSearchQuery);
      if (onExternalSearchComplete) {
        onExternalSearchComplete();
      }
    }
  }, [externalSearchQuery, onExternalSearchComplete]);

  // Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä Î≥ÄÍ≤ΩÎê† Îïå ÎèÑÏÑúÍ¥Ä Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (searchResults.length > 0 && selectedRegion) {
      const allLibraries = searchResults.reduce((acc, book) => {
        if (book.availability) {
          return acc.concat(book.availability.map((avail: any) => ({
            id: avail.libraryId,
            name: avail.libraryName,
            address: avail.libraryAddress,
            distance: Math.random() * 10,
            phone: avail.libraryPhone,
            hours: '09:00-18:00',
            status: avail.available ? 'open' : 'closed',
            type: 'public'
          })));
        }
        return acc;
      }, []);
      
      // Ï§ëÎ≥µ Ï†úÍ±∞
      const uniqueLibraries = allLibraries.filter((lib: any, index: number, self: any[]) => 
        index === self.findIndex((l: any) => l.id === lib.id)
      ).slice(0, 10);
      
      onLibrariesUpdate(uniqueLibraries);
    }
  }, [searchResults, selectedRegion, onLibrariesUpdate]);

  const generateLibraryAvailability = (libraryList: Library[]) => {
    return libraryList.slice(0, 3).map(lib => ({
      libraryId: lib.id,
      libraryName: lib.name,
      libraryAddress: lib.address,
      libraryPhone: lib.phone,
      available: Math.random() > 0.3,
      waitingList: Math.floor(Math.random() * 5) + 1
    }));
  };

  const performSearch = (query: string) => {
    setLoading(true);
    setHasSearched(true);
    
    // Ïã§Ï†ú Í≤ÄÏÉâÏñ¥Î•º Î∞òÏòÅÌïú ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
    const searchBooks = [
      { id: 1, title: `${query}Ïùò Ïù¥Ìï¥`, author: 'ÍπÄÎØºÏàò', publisher: 'ÌïôÎ¨∏ÏÇ¨' },
      { id: 2, title: `${query} ÏôÑÎ≤Ω Í∞ÄÏù¥Îìú`, author: 'Î∞ïÏòÅÌù¨', publisher: 'ÏßÄÏãùÏ∂úÌåêÏÇ¨' },
      { id: 3, title: `${query}ÏôÄ Ïã§Ï≤ú`, author: 'Ïù¥Ï†ïÌò∏', publisher: 'Ïã§Ïö©ÏÑúÏ†Å' }
    ];
    
    // Í≤ÄÏÉâÏñ¥Í∞Ä ÌäπÏ†ï ÌÇ§ÏõåÎìúÏù∏ Í≤ΩÏö∞ Ïã§Ï†ú Ï±Ö Ï†úÎ™© ÏÇ¨Ïö©
    let finalBooks = searchBooks;
    if (query.includes('ÏÜåÏÑ§') || query.includes('Î¨∏Ìïô')) {
      finalBooks = [
        { id: 1, title: '82ÎÖÑÏÉù ÍπÄÏßÄÏòÅ', author: 'Ï°∞ÎÇ®Ï£º', publisher: 'ÎØºÏùåÏÇ¨' },
        { id: 2, title: 'Î¥ÑÎÇ†ÏùÄ Í∞ÑÎã§', author: 'ÍπÄÏú†Ï†ï', publisher: 'Î¨∏ÌïôÍ≥ºÏßÄÏÑ±ÏÇ¨' },
        { id: 3, title: 'ÏïÑÎ≤ÑÏßÄÏùò Ìï¥Î∞©ÏùºÏßÄ', author: 'Ï†ïÏßÄÏïÑ', publisher: 'Ï∞ΩÎπÑ' }
      ];
    } else if (query.includes('Ïó≠ÏÇ¨') || query.includes('ÌïúÍµ≠ÏÇ¨')) {
      finalBooks = [
        { id: 1, title: 'ÌïúÍµ≠ÏÇ¨ Ìé∏ÏßÄ', author: 'Î∞ïÏùÄÎ¥â', publisher: 'Ï±ÖÍ≥ºÌï®Íªò' },
        { id: 2, title: 'Ï¥ù, Í∑†, Ïá†', author: 'Ïû¨Î†àÎìú Îã§Ïù¥ÏïÑÎ™¨Îìú', publisher: 'Î¨∏ÌïôÏÇ¨ÏÉÅ' },
        { id: 3, title: 'Ï°∞ÏÑ†ÏôïÏ°∞Ïã§Î°ù', author: 'Ïù¥ÎçïÏùº', publisher: 'Ïó≠ÏÇ¨ÎπÑÌèâÏÇ¨' }
      ];
    } else if (query.includes('Í≥ºÌïô') || query.includes('Í∏∞Ïà†')) {
      finalBooks = [
        { id: 1, title: 'ÏΩîÏä§Î™®Ïä§', author: 'Ïπº ÏÑ∏Ïù¥Í±¥', publisher: 'ÏÇ¨Ïù¥Ïñ∏Ïä§Î∂ÅÏä§' },
        { id: 2, title: 'Ïù¥Í∏∞Ï†Å Ïú†Ï†ÑÏûê', author: 'Î¶¨Ï≤òÎìú ÎèÑÌÇ®Ïä§', publisher: 'ÏùÑÏú†Î¨∏ÌôîÏÇ¨' },
        { id: 3, title: 'ÏÇ¨ÌîºÏóîÏä§', author: 'Ïú†Î∞ú ÌïòÎùºÎ¶¨', publisher: 'ÍπÄÏòÅÏÇ¨' }
      ];
    }
    
    setTimeout(() => {
      const results = finalBooks.map(book => ({
        ...book,
        availability: generateLibraryAvailability(libraries)
      }));
      setSearchResults(results);
      setLoading(false);
    }, 1000);
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;
    performSearch(searchTerm);
  };

  const handleReserve = (bookId: number, libraryId: number, libraryName: string, libraryPhone: string, isAvailable: boolean) => {
    const message = isAvailable 
      ? `${libraryName}ÏóêÏÑú Ï±ÖÏùÑ ÏòàÏïΩÌïòÏãúÍ≤†ÏäµÎãàÍπå?`
      : `${libraryName}Ïóê ÎåÄÍ∏∞Ïã†Ï≤≠ÏùÑ ÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
    
    if (window.confirm(message)) {
      window.location.href = `tel:${libraryPhone}`;
    }
  };

  return (
    <Container>
      <SearchForm onSubmit={handleSearch}>
        <SearchInput
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Ï∞æÍ≥† Ïã∂ÏùÄ Ï±Ö Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
        />
        <SearchButton type="submit" disabled={loading}>
          {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'üìö Ï±Ö Ï∞æÍ∏∞'}
        </SearchButton>
      </SearchForm>

      {searchResults.length > 0 && (
        <RegionSelector 
          onLibrariesUpdate={onLibrariesUpdate}
          selectedRegion={selectedRegion}
          onRegionUpdate={onRegionUpdate}
        />
      )}

      {hasSearched && searchResults.length === 0 && searchTerm && !loading && (
        <EmptyState>
          <div className="emoji">üìö</div>
          <h2>Ïïó! Ïù¥ Ï±ÖÏùÄ ÏóÜÏñ¥Ïöî</h2>
          <p>"{searchTerm}"Ïóê ÎåÄÌïú Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
          <button onClick={() => {
            setSearchTerm('');
            setSearchResults([]);
            setHasSearched(false);
          }}>
            Îã§Ïãú Í≤ÄÏÉâÌïòÍ∏∞
          </button>
        </EmptyState>
      )}

      {searchResults.length > 0 && (
        <div>
          {searchResults.map((book) => (
            <BookCard key={book.id}>
              <BookTitle>{book.title}</BookTitle>
              <BookInfo>
                <div>üìù Ï†ÄÏûê: {book.author}</div>
                <div>üè¢ Ï∂úÌåêÏÇ¨: {book.publisher}</div>
              </BookInfo>
              
              {selectedRegion ? (
                <div>
                  <h4 style={{ marginBottom: '12px', color: '#2d2d2d', fontSize: '14px' }}>
                    üìç {selectedRegion} ÎèÑÏÑúÍ¥Ä ÌòÑÌô©
                  </h4>
                  {book.availability.map((avail: any) => (
                    <LibraryCard key={avail.libraryId}>
                      <div className="library-header">
                        <div>
                          <div className="library-name">{avail.libraryName}</div>
                          <div className="library-address">{avail.libraryAddress}</div>
                        </div>
                        <div className="library-phone">{avail.libraryPhone}</div>
                      </div>
                      
                      <div className="library-actions">
                        <div className={`status-badge ${avail.available ? 'available' : 'waiting'}`}>
                          {avail.available ? 'üìö ÎåÄÏ∂úÍ∞ÄÎä•' : `‚è≥ ÎåÄÍ∏∞ ${avail.waitingList}Î™Ö`}
                        </div>
                        <button 
                          className={`action-button ${avail.available ? 'reserve' : 'wait'}`}
                          onClick={() => handleReserve(book.id, avail.libraryId, avail.libraryName, avail.libraryPhone, avail.available)}
                        >
                          {avail.available ? 'üìû ÏòàÏïΩ Ïã†Ï≤≠' : 'üìû ÎåÄÍ∏∞ Ïã†Ï≤≠'}
                        </button>
                      </div>
                    </LibraryCard>
                  ))}
                </div>
              ) : (
                <EmptyState>
                  <div className="emoji">üìç</div>
                  <p style={{ margin: 0, fontSize: '14px' }}>
                    ÏßÄÏó≠ ÏÑ†ÌÉù ÌõÑ ÎèÑÏÑúÍ¥Ä ÌòÑÌô©ÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî
                  </p>
                </EmptyState>
              )}
            </BookCard>
          ))}
        </div>
      )}
    </Container>
  );
};

export default BookSearchSection;
