  const popularRegions = ['ÏàòÏõêÏãú', 'ÏÑ±ÎÇ®Ïãú', 'Í≥†ÏñëÏãú', 'Ïö©Ïù∏Ïãú'];

  // ÏßÄÏó≠Î≥Ñ ÎçîÎØ∏ ÎèÑÏÑúÍ¥Ä Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
