import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import RegionSelector from './RegionSelector';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const SearchForm = styled.form`
  display: flex;
  gap: 12px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 8px;
  }
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 12px 16px;
  font-size: 16px;
  border: 2px solid #e5e5e5;
  border-radius: 8px;
  background: white;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 140, 74, 0.1);
  }
  
  @media (max-width: 768px) {
    padding: 10px 14px;
    font-size: 15px;
  }
`;

const SearchButton = styled.button`
  background: var(--primary-color);
  color: white;
  padding: 12px 20px;
  font-size: 16px;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;
  min-width: 100px;
  
  &:hover {
    background: #3a7538;
  }
  
  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }
  
  @media (max-width: 768px) {
    width: 100%;
    padding: 10px 16px;
    font-size: 15px;
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 40px 20px;
  
  .emoji {
    font-size: 48px;
    margin-bottom: 16px;
  }
  
  h2 {
    font-size: 18px;
    font-weight: bold;
    color: #2d2d2d;
    margin-bottom: 12px;
  }
  
  p {
    color: #666;
    margin-bottom: 20px;
    font-size: 14px;
  }
  
  button {
    padding: 10px 20px;
    background: var(--primary-color);
    color: white;
    border: none;
    border-radius: 6px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    
    &:hover {
      background: #3a7538;
    }
  }
`;

const BookCard = styled.div`
  background: #f8f9fa;
  border: 2px solid #e9ecef;
  border-radius: 12px;
  padding: 20px;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #4c8c4a;
    box-shadow: 0 4px 12px rgba(76, 140, 74, 0.1);
  }
  
  @media (max-width: 768px) {
    padding: 16px;
    border-radius: 8px;
  }
`;

const BookTitle = styled.h4`
  font-size: 18px;
  font-weight: 600;
  color: #2d2d2d;
  margin-bottom: 8px;
  
  @media (max-width: 768px) {
    font-size: 16px;
  }
`;

const BookInfo = styled.div`
  color: #666;
  margin-bottom: 16px;
  font-size: 14px;
  line-height: 1.5;
  
  div {
    margin-bottom: 4px;
  }
`;

const LibraryCard = styled.div`
  background: white;
  border: 1px solid #e9ecef;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 12px;
  
  .library-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 12px;
    
    @media (max-width: 768px) {
      flex-direction: column;
      gap: 8px;
    }
  }
  
  .library-name {
    font-weight: 600;
    color: #2d2d2d;
    font-size: 16px;
  }
  
  .library-address {
    color: #666;
    font-size: 14px;
    margin-top: 4px;
  }
  
  .library-phone {
    color: #666;
    font-size: 13px;
  }
  
  .library-actions {
    display: flex;
    align-items: center;
    gap: 12px;
    flex-wrap: wrap;
    
    @media (max-width: 768px) {
      flex-direction: column;
      align-items: stretch;
      gap: 8px;
    }
  }
  
  .status-badge {
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 13px;
    font-weight: 600;
    white-space: nowrap;
    
    &.available {
      background: #d4edda;
      color: #155724;
    }
    
    &.waiting {
      background: #f8d7da;
      color: #721c24;
    }
  }
  
  .action-button {
    padding: 8px 16px;
    font-size: 13px;
    font-weight: 600;
    border: none;
    border-radius: 6px;
    cursor: pointer;
    transition: all 0.3s ease;
    white-space: nowrap;
    
    &.reserve {
      background: var(--primary-color);
      color: white;
      
      &:hover {
        background: #3a7538;
      }
    }
    
    &.wait {
      background: #f28c28;
      color: white;
      
      &:hover {
        background: #e0761a;
      }
    }
    
    @media (max-width: 768px) {
      width: 100%;
      padding: 10px 16px;
    }
  }
`;

interface Library {
  id: number;
  name: string;
  address: string;
  distance: number;
  phone: string;
  hours: string;
  status: string;
  type?: string;
}

interface BookSearchSectionProps {
  libraries: Library[];
  onLibrariesUpdate: (libraries: Library[]) => void;
  selectedRegion: string;
  onRegionUpdate: (region: string) => void;
  externalSearchQuery?: string;
  onExternalSearchComplete?: () => void;
}

const BookSearchSection: React.FC<BookSearchSectionProps> = ({ 
  libraries, 
  onLibrariesUpdate, 
  selectedRegion, 
  onRegionUpdate,
  externalSearchQuery,
  onExternalSearchComplete
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    if (externalSearchQuery && externalSearchQuery !== searchTerm) {
      setSearchTerm(externalSearchQuery);
      performSearch(externalSearchQuery);
      if (onExternalSearchComplete) {
        onExternalSearchComplete();
      }
    }
  }, [externalSearchQuery, onExternalSearchComplete]);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    if (searchResults.length > 0 && selectedRegion) {
      const updatedResults = searchResults.map(book => ({
        ...book,
        availability: generateLibraryAvailability(libraries)
      }));
      setSearchResults(updatedResults);
    }
  }, [selectedRegion, libraries]);

  const generateLibraryAvailability = (libraryList: Library[]) => {
    return libraryList.slice(0, 3).map(library => ({
      libraryId: library.id,
      libraryName: library.name,
      libraryAddress: library.address,
      libraryPhone: library.phone,
      libraryType: library.type,
      available: Math.random() > 0.3,
      waitingList: Math.floor(Math.random() * 5) + 1
    }));
  };

  const performSearch = (query: string) => {
    setLoading(true);
    setHasSearched(true);
    
    const dummyBooks = [
      { id: 1, title: '달러구트 꿈 백화점', author: '이미예', publisher: '팩토리나인', isbn: '9791165341909' },
      { id: 2, title: '코스모스', author: '칼 세이건', publisher: '사이언스북스', isbn: '9788983711892' },
      { id: 3, title: '사피엔스', author: '유발 하라리', publisher: '김영사', isbn: '9788934972464' }
    ];

    const filteredBooks = dummyBooks.filter(book => 
      book.title.toLowerCase().includes(query.toLowerCase()) ||
      book.author.toLowerCase().includes(query.toLowerCase())
    );

    setTimeout(() => {
      const results = filteredBooks.length > 0 ? filteredBooks : dummyBooks.slice(0, 2);
      const resultsWithAvailability = results.map(book => ({
        ...book,
        availability: generateLibraryAvailability(libraries)
      }));

      setSearchResults(resultsWithAvailability);
      setLoading(false);
    }, 1000);
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      performSearch(searchTerm);
    }
  };

  const handleReserve = (bookId: number, libraryId: number, libraryName: string, libraryPhone: string, isAvailable: boolean) => {
    const action = isAvailable ? '예약' : '대기 신청';
    const message = `📚 ${action} 요청\n\n도서관: ${libraryName}\n전화번호: ${libraryPhone}\n\n${action} 신청을 위해 도서관에 직접 전화하시겠습니까?`;
    
    if (window.confirm(message)) {
      const phoneNumber = libraryPhone.replace(/[^0-9]/g, '');
      window.open(`tel:${phoneNumber}`, '_self');
    }
  };

  return (
    <Container>
      <SearchForm onSubmit={handleSearch}>
        <SearchInput
          type="text"
          placeholder="찾으시는 책 제목이나 저자명을 입력하세요"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <SearchButton type="submit" disabled={loading}>
          {loading ? '검색 중...' : '📚 책 찾기'}
        </SearchButton>
      </SearchForm>

      {searchResults.length > 0 && (
        <RegionSelector 
          onLibrariesUpdate={onLibrariesUpdate}
          selectedRegion={selectedRegion}
          onRegionUpdate={onRegionUpdate}
        />
      )}

      {hasSearched && searchResults.length === 0 && searchTerm && !loading && (
        <EmptyState>
          <div className="emoji">📚</div>
          <h2>앗! 이 책은 없어요</h2>
          <p>"{searchTerm}"에 대한 검색 결과가 없습니다.</p>
          <button onClick={() => {
            setSearchTerm('');
            setSearchResults([]);
            setHasSearched(false);
          }}>
            다시 검색하기
          </button>
        </EmptyState>
      )}

      {searchResults.length > 0 && (
        <div>
          {searchResults.map((book) => (
            <BookCard key={book.id}>
              <BookTitle>{book.title}</BookTitle>
              <BookInfo>
                <div>📝 저자: {book.author}</div>
                <div>🏢 출판사: {book.publisher}</div>
              </BookInfo>
              
              {selectedRegion ? (
                <div>
                  <h4 style={{ marginBottom: '12px', color: '#2d2d2d', fontSize: '14px' }}>
                    📍 {selectedRegion} 도서관 현황
                  </h4>
                  {book.availability.map((avail: any) => (
                    <LibraryCard key={avail.libraryId}>
                      <div className="library-header">
                        <div>
                          <div className="library-name">{avail.libraryName}</div>
                          <div className="library-address">{avail.libraryAddress}</div>
                        </div>
                        <div className="library-phone">{avail.libraryPhone}</div>
                      </div>
                      
                      <div className="library-actions">
                        <div className={`status-badge ${avail.available ? 'available' : 'waiting'}`}>
                          {avail.available ? '📚 대출가능' : `⏳ 대기 ${avail.waitingList}명`}
                        </div>
                        <button 
                          className={`action-button ${avail.available ? 'reserve' : 'wait'}`}
                          onClick={() => handleReserve(book.id, avail.libraryId, avail.libraryName, avail.libraryPhone, avail.available)}
                        >
                          {avail.available ? '📞 예약 신청' : '📞 대기 신청'}
                        </button>
                      </div>
                    </LibraryCard>
                  ))}
                </div>
              ) : (
                <EmptyState>
                  <div className="emoji">📍</div>
                  <p style={{ margin: 0, fontSize: '14px' }}>
                    지역 선택 후 도서관 현황을 확인하세요
                  </p>
                </EmptyState>
              )}
            </BookCard>
          ))}
        </div>
      )}
    </Container>
  );
};

export default BookSearchSection;
