import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import RegionSelector from './RegionSelector';

// Ïä§ÌÉÄÏùºÎìú Ïª¥Ìè¨ÎÑåÌä∏ Ï†ïÏùò
const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SearchForm = styled.form`
  display: flex;
  gap: 12px;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 8px;
  }
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 12px 16px;
  font-size: 16px;
  border: 2px solid #e5e5e5;
  border-radius: 8px;
  background: white;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;

  &:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 140, 74, 0.1);
  }

  @media (max-width: 768px) {
    padding: 10px 14px;
    font-size: 15px;
  }
`;

const SearchButton = styled.button`
  background: var(--primary-color);
  color: white;
  border: none;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  border-radius: 8px;
  cursor: pointer;
  white-space: nowrap;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;

  &:hover {
    background: #3a7538;
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }

  @media (max-width: 768px) {
    padding: 10px 20px;
    font-size: 15px;
  }
`;

const LoadingDiv = styled.div`
  text-align: center;
  padding: 40px;
  color: #666;
  font-size: 18px;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 60px 20px;
  color: #666;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;

  .emoji {
    font-size: 48px;
    margin-bottom: 16px;
  }

  .title {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 8px;
    color: #333;
  }

  .subtitle {
    font-size: 16px;
    color: #666;
    margin-bottom: 24px;
  }
`;

const SearchResults = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const BookCard = styled.div`
  background: white;
  border: 2px solid #e5e5e5;
  border-radius: 12px;
  padding: 20px;
  transition: all 0.3s ease;

  &:hover {
    border-color: var(--primary-color);
    box-shadow: 0 4px 12px rgba(76, 140, 74, 0.1);
  }

  @media (max-width: 768px) {
    padding: 16px;
  }
`;

const BookTitle = styled.h4`
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 8px;
  color: #333;
  line-height: 1.4;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;

  @media (max-width: 768px) {
    font-size: 16px;
  }
`;

const BookInfo = styled.div`
  display: flex;
  gap: 16px;
  margin-bottom: 16px;
  color: #666;
  font-size: 14px;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 4px;
  }
`;

const AvailabilitySection = styled.div`
  margin-top: 16px;
`;

const AvailabilityTitle = styled.h5`
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 12px;
  color: #333;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;

  @media (max-width: 768px) {
    font-size: 14px;
  }
`;

const LibraryList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const LibraryItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px;
  background: #f8f9fa;
  border-radius: 8px;

