import React, { useState, useEffect } from 'react';
import RegionSelector from './RegionSelector';

interface Library {
  id: number;
  name: string;
  address: string;
  distance: number;
  phone: string;
  hours: string;
  status: string;
  type?: string; // 도서관 타입 추가
}

interface BookSearchSectionProps {
  libraries: Library[];
  onLibrariesUpdate: (libraries: Library[]) => void;
  selectedRegion: string;
  onRegionUpdate: (region: string) => void;
  externalSearchQuery?: string;
  onExternalSearchComplete?: () => void;
}

const BookSearchSection: React.FC<BookSearchSectionProps> = ({ 
  libraries, 
  onLibrariesUpdate, 
  selectedRegion, 
  onRegionUpdate,
  externalSearchQuery,
  onExternalSearchComplete
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  // 외부에서 전달된 검색어 처리 (인기도서 클릭 시)
  useEffect(() => {
    if (externalSearchQuery && externalSearchQuery !== searchTerm) {
      setSearchTerm(externalSearchQuery);
      performSearch(externalSearchQuery);
      if (onExternalSearchComplete) {
        onExternalSearchComplete();
      }
    }
  }, [externalSearchQuery, searchTerm, onExternalSearchComplete]);

  // 지역이 변경되면 검색 결과 업데이트 (도서관 정보 갱신)
  useEffect(() => {
    if (searchResults.length > 0 && selectedRegion) {
      // 기존 검색 결과에 새로운 지역의 도서관 정보 업데이트
      const updatedResults = searchResults.map(book => ({
        ...book,
        availability: generateLibraryAvailability(libraries)
      }));
      setSearchResults(updatedResults);
    }
  }, [selectedRegion, libraries]);

  const generateLibraryAvailability = (libraryList: Library[]) => {
    return libraryList.slice(0, 5).map(library => ({
      libraryId: library.id,
      libraryName: library.name,
      libraryAddress: library.address,
      libraryPhone: library.phone,
      libraryType: library.type,
      available: Math.random() > 0.3,
      waitingList: Math.floor(Math.random() * 5) + 1
    }));
  };

  const performSearch = (query: string) => {
    setLoading(true);
    setHasSearched(true);
    
    // 더미 검색 결과 생성
    const dummyBooks = [
      { id: 1, title: '달러구트 꿈 백화점', author: '이미예', publisher: '팩토리나인', isbn: '9791165341909' },
      { id: 2, title: '코스모스', author: '칼 세이건', publisher: '사이언스북스', isbn: '9788983711892' },
      { id: 3, title: '사피엔스', author: '유발 하라리', publisher: '김영사', isbn: '9788934972464' },
      { id: 4, title: '호모 데우스', author: '유발 하라리', publisher: '김영사', isbn: '9788934985067' },
      { id: 5, title: '미드나잇 라이브러리', author: '매트 헤이그', publisher: '인플루엔셜', isbn: '9791189127893' },
      { id: 6, title: '아몬드', author: '손원평', publisher: '창비', isbn: '9788936434267' },
      { id: 7, title: '82년생 김지영', author: '조남주', publisher: '민음사', isbn: '9788937473395' },
      { id: 8, title: '죽은 시인의 사회', author: '톰 슐먼', publisher: '현대문학', isbn: '9788972757190' }
    ];

    // 검색어와 관련된 책들 필터링
    const filteredBooks = dummyBooks.filter(book => 
      book.title.toLowerCase().includes(query.toLowerCase()) ||
      book.author.toLowerCase().includes(query.toLowerCase()) ||
      book.publisher.toLowerCase().includes(query.toLowerCase())
    );

    setTimeout(() => {
      // 검색 결과가 없으면 랜덤으로 3개 선택
      const results = filteredBooks.length > 0 
        ? filteredBooks 
        : dummyBooks.sort(() => 0.5 - Math.random()).slice(0, 3);

      const resultsWithAvailability = results.map(book => ({
        ...book,
        availability: generateLibraryAvailability(libraries)
      }));

      setSearchResults(resultsWithAvailability);
      setLoading(false);
    }, 1000);
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      performSearch(searchTerm);
    }
  };

  const handleSearchTermChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const handleReserve = (bookId: number, libraryId: number, libraryName: string, libraryPhone: string, isAvailable: boolean) => {
    const action = isAvailable ? '예약' : '대기 신청';
    const message = `📚 ${action} 요청\n\n도서관: ${libraryName}\n전화번호: ${libraryPhone}\n\n${action} 신청을 위해 도서관에 직접 전화하시겠습니까?`;
    
    if (window.confirm(message)) {
      handlePhoneCall(libraryPhone, libraryName);
    }
  };

  const handlePhoneCall = (phone: string, libraryName: string) => {
    if (phone && phone !== 'N/A') {
      // 실제 전화 연결 (모바일에서 tel: 링크로 연결)
      const phoneNumber = phone.replace(/[^0-9]/g, ''); // 숫자만 추출
      window.open(`tel:${phoneNumber}`, '_self');
    } else {
      alert(`${libraryName}의 전화번호 정보가 없습니다.`);
    }
  };

  const getLibraryTypeStyle = (type: string) => {
    switch (type) {
      case '공공도서관':
        return 'bg-primary text-white';
      case '구립도서관':
        return 'bg-accent text-white';
      case '작은도서관':
        return 'bg-primary text-white';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="flex flex-col gap-8">
      <form onSubmit={handleSearch} className="flex gap-4 md:flex-col md:gap-3">
        <input
          type="text"
          placeholder="찾으시는 책 제목이나 저자명을 입력하세요"
          value={searchTerm}
          onChange={handleSearchTermChange}
          className="flex-1 px-4 py-3 text-base border-2 border-gray-300 rounded-lg bg-white transition-all duration-300 focus:outline-none focus:border-primary focus:shadow-lg placeholder-gray-500 md:px-4 md:py-3 sm:px-3 sm:py-2"
        />
        <button 
          type="submit" 
          disabled={loading}
          className="bg-primary text-white px-6 py-3 text-base font-semibold rounded-lg hover:bg-opacity-90 focus:outline-none focus:ring-3 focus:ring-primary/30 transition-all duration-300 whitespace-nowrap min-w-[120px] disabled:bg-gray-400 disabled:cursor-not-allowed md:w-full md:min-w-auto"
        >
          {loading ? '검색 중...' : '📚 책 찾기'}
        </button>
      </form>

      {/* 검색 완료 후 지역 선택 UI */}
      {searchResults.length > 0 && (
        <RegionSelector 
          onLibrariesUpdate={onLibrariesUpdate}
          selectedRegion={selectedRegion}
          onRegionUpdate={onRegionUpdate}
        />
      )}

      {/* 검색했지만 결과가 없을 때만 표시 */}
      {hasSearched && searchResults.length === 0 && searchTerm && !loading && (
        <div className="text-center py-16">
          <div className="text-6xl mb-6">📚</div>
          <h2 className="text-xl font-bold text-text mb-3">앗! 이 책은 없어요</h2>
          <p className="text-gray-600 mb-6">
            "{searchTerm}"에 대한 검색 결과가 없습니다.
          </p>
          <button 
            onClick={() => {
              setSearchTerm('');
              setSearchResults([]);
              setHasSearched(false);
            }}
            className="px-6 py-3 bg-primary text-white rounded-lg hover:bg-opacity-90 transition-all duration-300 font-medium"
          >
            다시 검색하기
          </button>
        </div>
      )}

      {searchResults.length > 0 && (
        <div className="flex flex-col gap-6">
          {searchResults.map((book) => (
            <div key={book.id} className="bg-gray-50 border-2 border-gray-200 rounded-xl p-6 transition-all duration-300 hover:border-green-500 hover:shadow-lg">
              <h4 className="text-xl font-semibold text-text mb-2 leading-tight md:text-lg sm:text-base">
                {book.title}
              </h4>
              <div className="text-base text-gray-600 mb-4 space-y-1 md:text-sm">
                <div>📝 저자: {book.author}</div>
                <div>🏢 출판사: {book.publisher}</div>
                <div>📖 ISBN: {book.isbn}</div>
              </div>
              
              <div className="flex flex-col gap-3">
                {selectedRegion ? (
                  <>
                    <h4 className="text-base font-medium text-text mb-2">
                      📍 {selectedRegion} 도서관 현황
                    </h4>
                    {book.availability.map((avail: any) => (
                      <div key={avail.libraryId} className="flex flex-col gap-3 p-4 bg-white rounded-lg border border-gray-200 shadow-sm">
                        <div className="flex items-start justify-between">
                          <div className="flex flex-col gap-1 flex-1">
                            <div className="flex items-center gap-2 flex-wrap">
                              <span className="text-base font-semibold text-text">
                                {avail.libraryName}
                              </span>
                              {avail.libraryType && (
                                <span className={`text-xs px-2 py-1 rounded-full font-medium ${getLibraryTypeStyle(avail.libraryType)}`}>
                                  {avail.libraryType}
                                </span>
                              )}
                            </div>
                            <span className="text-sm text-gray-600">{avail.libraryAddress}</span>
                          </div>
                          <span className="text-sm text-gray-600 flex-shrink-0 ml-2">{avail.libraryPhone}</span>
                        </div>
                        
                        <div className="flex items-center gap-3 flex-wrap">
                          <span className={`px-3 py-1 rounded-full text-sm font-semibold whitespace-nowrap ${
                            avail.available 
                              ? 'bg-green-100 text-green-800' 
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {avail.available ? '📚 대출가능' : `⏳ 대기 ${avail.waitingList}명`}
                          </span>
                          
                          <button 
                            onClick={() => handleReserve(book.id, avail.libraryId, avail.libraryName, avail.libraryPhone, avail.available)}
                            className={`px-4 py-2 text-sm font-semibold rounded-lg transition-all duration-300 whitespace-nowrap ${
                              avail.available 
                                ? 'bg-primary text-white hover:bg-opacity-90' 
                                : 'bg-accent text-white hover:bg-opacity-90'
                            }`}
                          >
                            {avail.available ? '📞 예약 신청' : '📞 대기 신청'}
                          </button>
                        </div>
                      </div>
                    ))}
                  </>
                ) : (
                  <div className="p-6 bg-gray-50 rounded-lg text-center">
                    <div className="text-4xl mb-4">📍</div>
                    <p className="text-base text-text font-medium">
                      지역 선택 후 도서관 현황을 확인하세요
                    </p>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BookSearchSection;
