      ],
      '처인구': [
        { id: '22', name: '용인시립중앙도서관', address: '용인시 처인구 중부대로 1199', phone: '031-324-4800', hours: '09:00-18:00' },
        { id: '33', name: '처인구립도서관', address: '용인시 처인구 삼가동 234', phone: '031-324-5900', hours: '09:00-18:00' },
      ]
