        description: 'ÌïúÍµ≠ Ïó¨ÏÑ±Ïùò ÌòÑÏã§ÏùÑ ÏÉùÏÉùÌïòÍ≤å Í∑∏Î¶∞ ÏÜåÏÑ§',
        loanCount: 1923
      },
      {
        id: '5',
        title: 'ÏÇ¨ÌîºÏóîÏä§',
        author: 'Ïú†Î∞ú ÌïòÎùºÎ¶¨',
        publisher: 'ÍπÄÏòÅÏÇ¨',
        isbn: '9788934972464',
        category: 'Ïù∏Î¨∏',
        publishYear: '2015',
        description: 'Ïù∏Î•òÏùò Ïó≠ÏÇ¨Î•º ÌÜµÏ∞∞Ìïú Î≤†Ïä§Ìä∏ÏÖÄÎü¨',
        loanCount: 1456
      },
      {
        id: '6',
        title: 'ÏõêÌîºÏä§ 1Í∂å',
        author: 'Ïò§Îã§ ÏóêÏù¥ÏπòÎ°ú',
        publisher: 'ÎåÄÏõêÏî®ÏïÑÏù¥',
        isbn: '9788925208015',
        category: 'ÎßåÌôî',
        publishYear: '1997',
        description: 'Ìï¥Ï†ÅÏôïÏùÑ ÍøàÍæ∏Îäî Î£®ÌîºÏùò Î™®Ìóò',
        loanCount: 1234
      }
    ];

    const filtered = dummyBooks.filter(book =>
      book.title.toLowerCase().includes(query.toLowerCase()) ||
      book.author.toLowerCase().includes(query.toLowerCase()) ||
      book.category.toLowerCase().includes(query.toLowerCase())
    );

    // Îß§Ïπ≠ÎêòÎäî Í≤∞Í≥ºÎßå Î∞òÌôò (Îß§Ïπ≠ÎêòÏßÄ ÏïäÏúºÎ©¥ Îπà Î∞∞Ïó¥)
    return filtered.map(book => ({
      ...book,
      availability: generateLibraryAvailability(libraries)
    }));
  };

  // Í≤ÄÏÉâ Ìèº Ï†úÏ∂ú
  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      // ÏÉàÎ°úÏö¥ Í≤ÄÏÉâ Ïãú Í∏∞Ï°¥ Í≤∞Í≥º Î∞è ÏßÄÏó≠ ÏÑ†ÌÉù Î¶¨ÏÖã
      setSearchResults([]);
      setHasSearched(false);
      setSelectedRegion('');
      setLibraries([]);

      saveRecentSearch(searchTerm.trim());
      performSearch(searchTerm.trim());
    }
  };

  // ÏòàÏïΩ Ï≤òÎ¶¨
  const handleReserve = (bookId: string, libraryId: number, libraryName: string, libraryPhone: string, libraryHours: string = '', isAvailable: boolean, availableBooks: number = 0, totalBooks: number = 0) => {
    // ÏòàÏïΩ ÎåÄÍ∏∞Îßå ÏûàÎäî Í≤ΩÏö∞ (availableBooks === 0 && totalBooks > 0) Î∞îÎ°ú Ï†ÑÌôî Ïó∞Í≤∞
    if (availableBooks === 0 && totalBooks > 0) {
      alert('ÎèÑÏÑúÍ¥ÄÏóê ÏßÅÏ†ë Ï†ÑÌôîÌïòÏó¨ ÎåÄÍ∏∞Ïã†Ï≤≠ÏùÑ ÏßÑÌñâÌïòÍ≤†ÏäµÎãàÎã§.');
      window.location.href = `tel:${libraryPhone}`;
      return;
    }

    const currentTime = new Date();
    const currentHour = currentTime.getHours();
    const isOpen = currentHour >= 9 && currentHour < 18;

    let message = '';
    if (isAvailable) {
      message = `${libraryName}ÏóêÏÑú Ïù¥ ÎèÑÏÑúÎ•º ÏòàÏïΩÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
    } else {
      message = `${libraryName}Ïóê ÎåÄÍ∏∞Ïã†Ï≤≠ÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
    }

    message += `\n\nüìû Ï†ÑÌôîÎ≤àÌò∏: ${libraryPhone}`;
    if (libraryHours) {
      message += `\n‚è∞ Ïö¥ÏòÅÏãúÍ∞Ñ: ${libraryHours}`;
    }

    if (!isOpen) {
      message += `\n\n‚ö†Ô∏è ÌòÑÏû¨ ÏãúÍ∞ÑÏùÄ ÎèÑÏÑúÍ¥Ä Ïö¥ÏòÅÏãúÍ∞Ñ Ïô∏ÏûÖÎãàÎã§.`;
      message += `\nÏö¥ÏòÅÏãúÍ∞ÑÏóê Îã§Ïãú Ï†ÑÌôîÌï¥Ï£ºÏÑ∏Ïöî.`;
    }

    const confirmed = window.confirm(message);
    if (confirmed) {
      if (isAvailable) {
        alert('ÏòàÏïΩ ÌôïÏù∏ÏùÑ ÏúÑÌï¥ ÎèÑÏÑúÍ¥ÄÏóê ÏßÅÏ†ë Ï†ÑÌôîÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.');
      } else {
        alert('ÎåÄÍ∏∞Ïã†Ï≤≠ ÌôïÏù∏ÏùÑ ÏúÑÌï¥ ÎèÑÏÑúÍ¥ÄÏóê ÏßÅÏ†ë Ï†ÑÌôîÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.');
      }
      window.location.href = `tel:${libraryPhone}`;
    }
  };

  // Ïô∏Î∂Ä Í≤ÄÏÉâÏñ¥ Ï≤òÎ¶¨
  useEffect(() => {
    if (externalSearchQuery && externalSearchQuery.trim()) {
      setSearchTerm(externalSearchQuery);
      performSearch(externalSearchQuery);
      if (onExternalSearchComplete) {
        onExternalSearchComplete();
      }
    }
  }, [externalSearchQuery, onExternalSearchComplete, performSearch]);

  // Î∂ÄÎ™®ÏóêÏÑú Ï†ÑÎã¨Î∞õÏùÄ ÏßÄÏó≠ Ï†ïÎ≥¥ ÎèôÍ∏∞Ìôî
  useEffect(() => {
    if (parentSelectedRegion !== undefined) {
      setSelectedRegion(parentSelectedRegion);
    }
  }, [parentSelectedRegion]);

  // ÏßÄÏó≠ ÏÑ†ÌÉù Ï≤òÎ¶¨
  useEffect(() => {
    if (selectedRegion) {
      const regionLibraries = generateLibrariesByRegion(selectedRegion);
      setLibraries(regionLibraries);
    }
  }, [selectedRegion]);



  // ÏßÄÏó≠Ïù¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Í≤ÄÏÉâ Í≤∞Í≥º ÏóÖÎç∞Ïù¥Ìä∏
  const updateSearchResultsWithLibraries = React.useCallback((newLibraries: Library[]) => {
    setSearchResults(prevResults => {
      if (prevResults.length > 0 && newLibraries.length > 0) {
        return prevResults.map(book => ({
          ...book,
          availability: generateLibraryAvailability(newLibraries)
        }));
      }
      return prevResults;
    });
  }, []);

  // ÎèÑÏÑúÍ¥Ä Ï†ïÎ≥¥Í∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Í≤ÄÏÉâ Í≤∞Í≥º ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (libraries.length > 0) {
      updateSearchResultsWithLibraries(libraries);
    }
  }, [libraries, updateSearchResultsWithLibraries]); // librariesÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßå Ïã§Ìñâ

  return (
    <Container>
      <SearchForm onSubmit={handleSearch}>
        <SearchInputContainer>
          <SearchInput
            type="text"
            placeholder="Ï∞æÏúºÏãúÎäî ÎèÑÏÑúÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
            value={searchTerm}
            onChange={handleSearchInputChange}
            onFocus={handleSearchInputFocus}
            onBlur={handleAutocompleteClose}
          />
          {showAutocomplete && autocompleteItems.length > 0 && (
            <AutocompleteDropdown>
              {autocompleteItems.map((item, index) => (
                <AutocompleteItemElement key={index} onClick={() => handleAutocompleteSelect(item)}>
                  <span className="icon">
                    {item.type === 'recent' ? 'üïê' : item.type === 'popular' ? 'üî•' : 'üí°'}
                  </span>
                  <span className="text">{item.text}</span>
                  <span className="category">{item.category}</span>
                </AutocompleteItemElement>
              ))}
            </AutocompleteDropdown>
          )}
        </SearchInputContainer>
        <SearchButton type="submit" disabled={loading}>
          {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'üîç Í≤ÄÏÉâ'}
        </SearchButton>
      </SearchForm>

      {/* Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏûàÏùÑ ÎïåÎßå ÏßÄÏó≠ ÏÑ†ÌÉùÍ∏∞ ÌëúÏãú */}
      {searchResults.length > 0 && (
        <div style={{ marginBottom: '20px' }}>
          <RegionSelector 
            onLibrariesUpdate={setLibraries} 
            selectedRegion={selectedRegion} 
            onRegionUpdate={(region) => {
              setSelectedRegion(region);
              if (onRegionUpdate) {
                onRegionUpdate(region);
              }
              // ÏßÄÏó≠ Î≥ÄÍ≤Ω Ïãú Ï¶âÏãú ÎèÑÏÑúÍ¥Ä Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
              const regionLibraries = generateLibrariesByRegion(region);
              setLibraries(regionLibraries);
            }} 
          />
        </div>
      )}



      {apiStatus && (
        <ApiStatusBanner status={apiStatus.includes('‚ö†Ô∏è') || apiStatus.includes('‚ùå') ? 'warning' : 'success'}>
          <ApiStatusIcon>{apiStatus.includes('‚ö†Ô∏è') || apiStatus.includes('‚ùå') ? '‚ö†Ô∏è' : '‚úÖ'}</ApiStatusIcon>
          {apiStatus}
                     {apiStatus.includes('‚ö†Ô∏è') || apiStatus.includes('‚ùå') ? (
             <ApiTestButton onClick={async () => {
               setApiStatus('üîÑ API ÏÉÅÌÉú ÌôïÏù∏ Ï§ë...');
               try {
                 const connectionTest = await testApiConnection();
                 setApiStatus(connectionTest.message);
               } catch (error) {
                 setApiStatus('‚ùå API Ïó∞Í≤∞ Ïã§Ìå®');
               }
             }}>
               Îã§Ïãú ÌôïÏù∏
             </ApiTestButton>
           ) : null}
        </ApiStatusBanner>
      )}

      {loading && (
        <LoadingDiv>
          üìö Ïã§Ï†ú ÎèÑÏÑúÍ¥Ä APIÏóêÏÑú Í≤ÄÏÉâÌïòÍ≥† ÏûàÏäµÎãàÎã§...
        </LoadingDiv>
      )}

      {!loading && hasSearched && searchResults.length === 0 && (
        <EmptyState>
          <div className="emoji">üìñ</div>
          <div className="title">Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§</div>
          <div className="subtitle">Îã§Î•∏ ÌÇ§ÏõåÎìúÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî</div>
        </EmptyState>
      )}

      {!loading && searchResults.length > 0 && (
        <SearchResults>
          {searchResults.map((book) => (
            <BookCard key={book.id}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '15px' }}>
                <BookTitle>{book.title}</BookTitle>
                <button
                  onClick={() => toggleBookmark(book.id)}
                  style={{
                    background: 'none',
                    border: 'none',
                    fontSize: '24px',
                    cursor: 'pointer',
                    color: isBookmarked(book.id) ? '#ff6b6b' : '#ccc',
                    transition: 'color 0.3s'
                  }}
                  title={isBookmarked(book.id) ? 'Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú' : 'Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä'}
                >
                  {isBookmarked(book.id) ? '‚ù§Ô∏è' : 'ü§ç'}
                </button>
              </div>
              <BookInfo>
                <span>Ï†ÄÏûê: {book.author}</span>
                <span>Ï∂úÌåêÏÇ¨: {book.publisher}</span>
                <span>Ïπ¥ÌÖåÍ≥†Î¶¨: {book.category}</span>
                <span>Ï∂úÍ∞ÑÎÖÑÎèÑ: {book.publishYear}</span>
                {book.loanCount && <span>ÎåÄÏ∂ú ÌöüÏàò: {book.loanCount.toLocaleString()}Ìöå</span>}
              </BookInfo>

              {book.description && (
                <BookDescription>
                  {book.description}
                </BookDescription>
              )}

              {selectedRegion && (
                <AvailabilitySection>
                  <AvailabilityTitle>
                    üìç {selectedRegion} ÎèÑÏÑúÍ¥Ä ÏÜåÏû• ÌòÑÌô©
                  </AvailabilityTitle>
                  <LibraryList>
                    {book.availability
                    .sort((a, b) => {
                      if (a.available && !b.available) return -1;
                      if (!a.available && b.available) return 1;
                      if (a.available && b.available) return a.waitingList - b.waitingList;
                      return a.waitingList - b.waitingList;
                    })
                    .map((avail) => (
                    <LibraryItem key={avail.libraryId}>
                      <LibraryHeader>
                        <LibraryName>
                          {avail.available ? '‚úÖ ' : '‚ùå '}
                          {avail.libraryName}
                        </LibraryName>
                        <LibraryAddress>üìç {avail.libraryAddress}</LibraryAddress>
                        <LibraryPhone>üìû {avail.libraryPhone}</LibraryPhone>
                        <LibraryHours>‚è∞ {avail.libraryHours || '09:00-18:00'}</LibraryHours>
                      </LibraryHeader>
                      <LibraryStats>
                        {avail.availableBooks > 0 && (
                          <div style={{ 
                            background: '#e8f5e8', 
                            border: '1px solid #4CAF50',
                            borderRadius: '8px',
                            padding: '12px',
                            marginBottom: '10px'
                          }}>
                            <div style={{ 
                              display: 'flex', 
                              alignItems: 'center', 
                              gap: '8px',
                              marginBottom: '8px',
                              fontWeight: 'bold',
                              color: '#2e7d32'
                            }}>
                              üìñ ÎåÄÏ∂ú Í∞ÄÎä•Ìïú ÎèÑÏÑú ÏúÑÏπò
                            </div>
                            <div style={{ 
                              fontSize: '14px',
                              color: '#1b5e20',
                              lineHeight: '1.4'
                            }}>
                              üìç {avail.location}
                            </div>
                            <div style={{ 
                              fontSize: '14px',
                              color: '#2e7d32',
                              marginTop: '4px'
                            }}>
                              ‚úÖ ÎåÄÏ∂ú Í∞ÄÎä• {avail.availableBooks}Í∂å
                            </div>
                          </div>
                        )}
                      </LibraryStats>

                      {avail.availableBooks === 0 && avail.totalBooks > 0 && (
                        <div style={{ 
                          background: '#e8f5e8', 
                          border: '1px solid #4CAF50',
                          borderRadius: '8px',
                          padding: '12px',
                          margin: '10px 0',
                          fontSize: '14px',
                          color: '#2e7d32'
                        }}>
                          üìû ÌòÑÏû¨ Î™®Îì† ÎèÑÏÑúÍ∞Ä ÎåÄÏ∂ú Ï§ëÏûÖÎãàÎã§.
                        </div>
                      )}

                      {avail.totalBooks === 0 && (
                        <div style={{ 
                          background: '#f8d7da', 
                          border: '1px solid #f5c6cb',
                          borderRadius: '8px',
                          padding: '12px',
                          margin: '10px 0',
                          fontSize: '14px',
                          color: '#721c24'
                        }}>
                          ‚ùå Ïù¥ ÎèÑÏÑúÍ¥ÄÏóêÎäî Ìï¥Îãπ ÎèÑÏÑúÍ∞Ä ÏÜåÏû•ÎêòÏñ¥ ÏûàÏßÄ ÏïäÏäµÎãàÎã§.
                        </div>
                      )}

                      <div style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        marginBottom: '10px'
                      }}>
                        {avail.waitingList > 0 && (
                          <div style={{ 
                            fontSize: '14px',
                            fontWeight: 'bold',
                            color: 'var(--accent-color)',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '6px'
                          }}>
                            ‚è≥ ÏòàÏïΩ ÎåÄÍ∏∞ {avail.waitingList}Î™Ö
                          </div>
                        )}
                        <div style={{ marginLeft: 'auto' }}>
                          <ReserveButton 
                            available={avail.available || (avail.availableBooks === 0 && avail.totalBooks > 0)}
                            onClick={() => handleReserve(
                              book.id,
                              avail.libraryId,
                              avail.libraryName,
                              avail.libraryPhone,
                              avail.libraryHours || '09:00-18:00',
                              avail.available,
                              avail.availableBooks,
                              avail.totalBooks
                            )}
                            disabled={avail.totalBooks === 0}
                          >
                            {avail.totalBooks === 0 ? 'üìµ ÏÜåÏû• ÏóÜÏùå' : 'üìñ Î∞îÎ°ú ÏòàÏïΩ'}
                          </ReserveButton>
                        </div>
                      </div>
                    </LibraryItem>
                  ))}
                </LibraryList>
              </AvailabilitySection>
            )}
            </BookCard>
          ))}
        </SearchResults>
      )}
    </Container>
  );
};

export default BookSearchSection;
