import React from 'react';
import styled from 'styled-components';

const HeaderContainer = styled.header`
  background-color: var(--primary-color);
  color: white;
  padding: 24px;
  text-align: center;
  margin-bottom: 32px;
  border-radius: 12px;
  box-shadow: var(--shadow);
  
  @media (max-width: 768px) {
    padding: 16px;
    margin-bottom: 24px;
  }
`;

const Title = styled.h1`
  font-size: 28px;
  font-weight: bold;
  color: white;
  margin-bottom: 12px;
  
  @media (max-width: 768px) {
    font-size: 24px;
  }
`;

const Subtitle = styled.p`
  font-size: 16px;
  line-height: 1.6;
  color: white;
  opacity: 0.9;
  margin-bottom: 16px;
  
  @media (max-width: 768px) {
    font-size: 14px;
  }
`;

const Header: React.FC = () => {
  return (
    <HeaderContainer>
      <Title>북소리</Title>
      <Subtitle>우리 동네 도서관 책을 한눈에, 함께 읽는 사람도 함께 찾아보세요.</Subtitle>
    </HeaderContainer>
  );
};

export default Header;
