import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import RegionSelector from './RegionSelector';
import { testApiConnection, getApiStatus, fetchPopularBooks, PopularBookData } from '../services/LibraryAPI';

// Ïä§ÌÉÄÏùºÎìú Ïª¥Ìè¨ÎÑåÌä∏ Ï†ïÏùò
const Container = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const SearchForm = styled.form`
  display: flex;
  gap: 15px;
  margin-bottom: 25px;
  flex-wrap: wrap;
`;

const SearchInputContainer = styled.div`
  position: relative;
  flex: 1;
  min-width: 300px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 15px 20px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 18px;
  outline: none;
  transition: border-color 0.3s;

  &:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 175, 80, 0.1);
  }

  &::placeholder {
    color: #666;
    font-size: 16px;
  }
`;

const AutocompleteDropdown = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  background: white;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  max-height: 300px;
  overflow-y: auto;
  z-index: 1000;
  margin-top: 4px;
`;

const AutocompleteItemElement = styled.div`
  padding: 12px 16px;
  cursor: pointer;
  border-bottom: 1px solid #f0f0f0;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 16px;

  &:hover {
    background: #f8f9fa;
  }

  &:last-child {
    border-bottom: none;
  }

  .icon {
    color: #666;
    font-size: 14px;
  }

  .text {
    flex: 1;
  }

  .category {
    color: #999;
    font-size: 12px;
  }
`;

const SearchButton = styled.button`
  padding: 15px 30px;
  background: var(--primary-color);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 18px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;
  white-space: nowrap;

  &:hover {
    background: #45a049;
    transform: translateY(-2px);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
  }
`;



const LoadingDiv = styled.div`
  text-align: center;
  padding: 40px;
  font-size: 18px;
  color: #666;
  background: white;
  border-radius: 8px;
  margin: 20px 0;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 60px 20px;
  background: white;
  border-radius: 8px;
  margin: 20px 0;

  .emoji {
    font-size: 48px;
    margin-bottom: 20px;
  }

  .title {
    font-size: 24px;
    font-weight: 600;
    color: #333;
    margin-bottom: 10px;
  }

  .subtitle {
    font-size: 16px;
    color: #666;
  }
`;

const SearchResults = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const BookCard = styled.div`
  background: white;
  border-radius: 12px;
  padding: 25px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid #e0e0e0;
  transition: transform 0.3s, box-shadow 0.3s;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
  }
`;

const BookTitle = styled.h3`
  margin: 0;
  color: #333;
  font-size: 20px;
  font-weight: 600;
  line-height: 1.4;
  flex: 1;
`;

const BookInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-bottom: 15px;

  span {
    color: #666;
    font-size: 14px;
  }
`;

const BookDescription = styled.p`
  color: #555;
  font-size: 14px;
  line-height: 1.5;
  margin-bottom: 20px;
  padding: 15px;
  background: #f8f9fa;
  border-radius: 8px;
`;

const AvailabilitySection = styled.div`
  border-top: 1px solid #e0e0e0;
  padding-top: 20px;
`;

const AvailabilityTitle = styled.h4`
  margin: 0 0 15px 0;
  color: #333;
  font-size: 16px;
  font-weight: 600;
`;

const LibraryList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const LibraryItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  background: #fafafa;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
  }
`;

const LibraryHeader = styled.div`
  flex: 1;
  margin-bottom: 10px;
`;

const LibraryName = styled.h4`
  margin: 0 0 4px 0;
  color: #333;
  font-size: 18px;
  font-weight: 600;
`;

const LibraryAddress = styled.p`
  margin: 0 0 4px 0;
  color: #666;
  font-size: 14px;
`;

const LibraryPhone = styled.p`
  margin: 0 0 4px 0;
  color: #4CAF50;
  font-size: 14px;
  font-weight: 600;
`;

const LibraryHours = styled.p`
  margin: 0 0 4px 0;
  color: #666;
  font-size: 12px;
`;

const LibraryStats = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-bottom: 15px;
  padding: 10px 0;
  border-top: 1px solid #eee;
  border-bottom: 1px solid #eee;
`;

// ActionButtons Ïä§ÌÉÄÏùºÎìú Ïª¥Ìè¨ÎÑåÌä∏ Ï†úÍ±∞ (ÏÇ¨Ïö©ÌïòÏßÄ ÏïäÏùå)

const ReserveButton = styled.button<{ available: boolean }>`
  padding: 8px 15px;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;
  white-space: nowrap;

  background: ${props => props.available ? '#4CAF50' : '#ff9800'};
  color: white;

  &:hover:not(:disabled) {
    background: ${props => props.available ? '#45a049' : '#f57c00'};
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    opacity: 0.6;
  }
`;

const ApiStatusBanner = styled.div<{ status: 'success' | 'warning' | 'error' }>`
  padding: 12px 20px;
  border-radius: 8px;
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  gap: 10px;
  font-size: 14px;
  font-weight: 500;

  ${props => {
    switch (props.status) {
      case 'success':
        return `
          background: #e8f5e8;
          color: #2e7d32;
          border: 1px solid #4caf50;
        `;
      case 'warning':
        return `
          background: #fff8e1;
          color: #f57c00;
          border: 1px solid #ff9800;
        `;
      case 'error':
        return `
          background: #ffebee;
          color: #c62828;
          border: 1px solid #f44336;
        `;
      default:
        return `
          background: #f5f5f5;
          color: #666;
          border: 1px solid #ddd;
        `;
    }
  }}
`;

const ApiStatusIcon = styled.span`
  font-size: 18px;
`;

const ApiTestButton = styled.button`
  margin-left: auto;
  padding: 6px 12px;
  border: none;
  border-radius: 4px;
  background: var(--primary-color);
  color: white;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background: #45a049;
    transform: translateY(-1px);
  }
`;


// Ïù∏ÌÑ∞ÌéòÏù¥Ïä§ Ï†ïÏùò
interface Library {
  id: number;
  name: string;
  address: string;
  phone: string;
  hours?: string;
}

interface AutocompleteItemData {
  text: string;
  category: string;
  type: 'popular' | 'recent' | 'suggestion';
}

interface Book {
  id: string;
  title: string;
  author: string;
  publisher: string;
  isbn: string;
  category: string;
  publishYear: string;
  description?: string;
  imageUrl?: string;
  loanCount?: number;
  availability: Array<{
    libraryId: number;
    libraryName: string;
    libraryAddress: string;
    libraryPhone: string;
    libraryHours?: string;
    available: boolean;
    waitingList: number;
    totalBooks: number;
    availableBooks: number;
    location: string;
  }>;
}

interface BookSearchSectionProps {
  externalSearchQuery?: string;
  onExternalSearchComplete?: () => void;
  selectedRegion?: string;
  onRegionUpdate?: (region: string) => void;
}

const BookSearchSection: React.FC<BookSearchSectionProps> = ({ 
  externalSearchQuery, 
  onExternalSearchComplete,
  selectedRegion: parentSelectedRegion,
  onRegionUpdate 
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<Book[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const [selectedRegion, setSelectedRegion] = useState(parentSelectedRegion || '');
  const [libraries, setLibraries] = useState<Library[]>([]);
  const [apiStatus, setApiStatus] = useState<string>('');

  // ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ ÏÉÅÌÉú
  const [recentSearches, setRecentSearches] = useState<string[]>([]);

  // ÏûêÎèôÏôÑÏÑ± ÏÉÅÌÉú
  const [showAutocomplete, setShowAutocomplete] = useState(false);
  const [autocompleteItems, setAutocompleteItems] = useState<AutocompleteItemData[]>([]);

  // Ï¶êÍ≤®Ï∞æÍ∏∞ ÏÉÅÌÉú
  const [bookmarks, setBookmarks] = useState<string[]>([]);



  // Ïù∏Í∏∞ Í≤ÄÏÉâÏñ¥ Î™©Î°ù
  const popularSearches = [
    { text: 'Îã¨Í≥º 6ÌéúÏä§', category: 'Î¨∏Ìïô' },
    { text: 'ÎØ∏ÏõÄÎ∞õÏùÑ Ïö©Í∏∞', category: 'ÏûêÍ∏∞Í≥ÑÎ∞ú' },
    { text: 'ÏàòÌïôÏùò Ï†ïÏÑù', category: 'ÍµêÏú°' },
    { text: '82ÎÖÑÏÉù ÍπÄÏßÄÏòÅ', category: 'ÏÜåÏÑ§' },
    { text: 'ÏõêÏîΩ', category: 'ÏûêÍ∏∞Í≥ÑÎ∞ú' },
    { text: 'ÏÑ∏Í≥ÑÏÇ¨ Ìé∏ÏßÄ', category: 'Ïó≠ÏÇ¨' },
    { text: 'ÏΩîÏä§Î™®Ïä§', category: 'Í≥ºÌïô' },
    { text: 'ÏûëÎ≥ÑÌïòÏßÄ ÏïäÎäîÎã§', category: 'ÏÜåÏÑ§' },
    { text: 'ÏïÑÌîÑÎãàÍπå Ï≤≠Ï∂òÏù¥Îã§', category: 'ÏóêÏÑ∏Ïù¥' },
    { text: 'Î∂àÌé∏Ìïú Ìé∏ÏùòÏ†ê', category: 'ÏÜåÏÑ§' },
    { text: 'ÏïÑÎ™¨Îìú', category: 'ÏÜåÏÑ§' },
    { text: 'ÎãπÏã†Ïù¥ Ïò≥Îã§', category: 'ÏóêÏÑ∏Ïù¥' },
    { text: 'ÏÇ¨ÌîºÏóîÏä§', category: 'Ïù∏Î¨∏' },
    { text: 'Îç∞ÎØ∏Ïïà', category: 'Î¨∏Ìïô' },
    { text: 'Ïñ¥Î¶∞ÏôïÏûê', category: 'Î¨∏Ìïô' }
  ];

  // ÏûêÎèôÏôÑÏÑ± Í∏∞Îä•
  const updateAutocomplete = (inputValue: string) => {
    if (!inputValue.trim()) {
      const items: AutocompleteItemData[] = [
        ...recentSearches.slice(0, 5).map(search => ({
          text: search,
          category: 'ÏµúÍ∑º Í≤ÄÏÉâ',
          type: 'recent' as const
        })),
        ...popularSearches.slice(0, 5).map(search => ({
          text: search.text,
          category: search.category,
          type: 'popular' as const
        }))
      ];
      setAutocompleteItems(items);
      setShowAutocomplete(true);
      return;
    }

    const searchValue = inputValue.toLowerCase();
    const matchedItems: AutocompleteItemData[] = [];

    recentSearches.forEach(search => {
      if (search.toLowerCase().includes(searchValue)) {
        matchedItems.push({
          text: search,
          category: 'ÏµúÍ∑º Í≤ÄÏÉâ',
          type: 'recent'
        });
      }
    });

    popularSearches.forEach(search => {
      if (search.text.toLowerCase().includes(searchValue)) {
        matchedItems.push({
          text: search.text,
          category: search.category,
          type: 'popular'
        });
      }
    });

    setAutocompleteItems(matchedItems.slice(0, 10));
    setShowAutocomplete(matchedItems.length > 0);
  };

  // ÏûêÎèôÏôÑÏÑ± ÏïÑÏù¥ÌÖú ÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleAutocompleteSelect = (item: AutocompleteItemData) => {
    setSearchTerm(item.text);
    setShowAutocomplete(false);
    saveRecentSearch(item.text);
    performSearch(item.text);
  };

  // Í≤ÄÏÉâ ÏûÖÎ†• Ï≤òÎ¶¨
  const handleSearchInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchTerm(value);

    setTimeout(() => {
      updateAutocomplete(value);
    }, 300);
  };

  // Í≤ÄÏÉâ ÏûÖÎ†• Ìè¨Ïª§Ïä§ Ï≤òÎ¶¨
  const handleSearchInputFocus = () => {
    updateAutocomplete(searchTerm);
  };

  // ÏûêÎèôÏôÑÏÑ± Îã´Í∏∞
  const handleAutocompleteClose = () => {
    setTimeout(() => setShowAutocomplete(false), 150);
  };

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ Î∞è Ï¶êÍ≤®Ï∞æÍ∏∞ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const savedSearches = localStorage.getItem('recentSearches');
    if (savedSearches) {
      setRecentSearches(JSON.parse(savedSearches));
    }

    const savedBookmarks = localStorage.getItem('bookmarks');
