// 인터페이스 정의
interface Library {
  id: number;
  name: string;
  address: string;
  phone: string;
  hours?: string;
}

interface AutocompleteItemData {
  text: string;
  category: string;
  type: 'popular' | 'recent' | 'suggestion';
}

interface Book {
  id: string;
  title: string;
  author: string;
  publisher: string;
  isbn: string;
  category: string;
  publishYear: string;
  description?: string;
  imageUrl?: string;
  loanCount?: number;
  availability: Array<{
    libraryId: number;
    libraryName: string;
    libraryAddress: string;
    libraryPhone: string;
    libraryHours?: string;
    available: boolean;
    waitingList: number;
    totalBooks: number;
    availableBooks: number;
    location: string;
  }>;
}

interface BookSearchSectionProps {
  externalSearchQuery?: string;
  onExternalSearchComplete?: () => void;
  selectedRegion?: string;
  onRegionUpdate?: (region: string) => void;
}

const BookSearchSection: React.FC<BookSearchSectionProps> = ({ 
  externalSearchQuery, 
  onExternalSearchComplete,
  selectedRegion: parentSelectedRegion,
  onRegionUpdate 
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<Book[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const [selectedRegion, setSelectedRegion] = useState(parentSelectedRegion || '');
