import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import RegionSelector from './RegionSelector';

// Ïä§ÌÉÄÏùºÎìú Ïª¥Ìè¨ÎÑåÌä∏ Ï†ïÏùò
const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SearchForm = styled.form`
  display: flex;
  gap: 12px;
  margin-bottom: 20px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 8px;
  }
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 12px 16px;
  font-size: 16px;
  border: 2px solid #e5e5e5;
  border-radius: 8px;
  background: white;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  &:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 140, 74, 0.1);
  }
  
  @media (max-width: 768px) {
    padding: 10px 14px;
    font-size: 15px;
  }
`;

const SearchButton = styled.button`
  background: var(--primary-color);
  color: white;
  border: none;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  border-radius: 8px;
  cursor: pointer;
  white-space: nowrap;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  &:hover {
    background: #3a7538;
  }
  
  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }
  
  @media (max-width: 768px) {
    padding: 10px 20px;
    font-size: 15px;
  }
`;

const LoadingDiv = styled.div`
  text-align: center;
  padding: 40px;
  color: #666;
  font-size: 18px;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 60px 20px;
  color: #666;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  .emoji {
    font-size: 48px;
    margin-bottom: 16px;
  }
  
  .title {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 8px;
    color: #333;
  }
  
  .subtitle {
    font-size: 16px;
    color: #666;
    margin-bottom: 24px;
  }
`;

const SearchResults = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const BookCard = styled.div`
  background: white;
  border: 2px solid #e5e5e5;
  border-radius: 12px;
  padding: 20px;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: var(--primary-color);
    box-shadow: 0 4px 12px rgba(76, 140, 74, 0.1);
  }
  
  @media (max-width: 768px) {
    padding: 16px;
  }
`;

const BookTitle = styled.h4`
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 8px;
  color: #333;
  line-height: 1.4;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  @media (max-width: 768px) {
    font-size: 16px;
  }
`;

const BookInfo = styled.div`
  display: flex;
  gap: 16px;
  margin-bottom: 16px;
  color: #666;
  font-size: 14px;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 4px;
  }
`;

const AvailabilitySection = styled.div`
  margin-top: 16px;
`;

const AvailabilityTitle = styled.h5`
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 12px;
  color: #333;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  @media (max-width: 768px) {
    font-size: 14px;
  }
`;

const LibraryList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const LibraryItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px;
  background: #f8f9fa;
  border-radius: 8px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
    gap: 8px;
  }
`;

const LibraryInfo = styled.div`
  flex: 1;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  .name {
    font-weight: 600;
    color: #333;
    margin-bottom: 4px;
  }
  
  .address {
    font-size: 13px;
    color: #666;
  }
`;

const StatusButton = styled.button`
  padding: 8px 16px;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  font-family: 'Pretendard', 'Arita Dotum', 'Noto Sans KR', sans-serif;
  
  &.available {
    background: var(--primary-color);
    color: white;
  }
  
  &.unavailable {
    background: var(--accent-color);
    color: white;
  }
  
  &:hover {
    opacity: 0.9;
  }
  
  @media (max-width: 768px) {
    width: 100%;
    justify-self: stretch;
  }
`;

// ÌÉÄÏûÖ Ï†ïÏùò
interface Book {
  id: number;
  title: string;
  author: string;
  publisher: string;
  availability: LibraryAvailability[];
}

interface LibraryAvailability {
  libraryId: number;
  libraryName: string;
  libraryAddress: string;
  libraryPhone: string;
  available: boolean;
  waitingList: number;
}

interface Library {
  id: number;
  name: string;
  address: string;
  phone: string;
}

const BookSearchSection: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<Book[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const [selectedRegion, setSelectedRegion] = useState<string>('');
  const [libraries, setLibraries] = useState<Library[]>([]);

  // ÏßÄÏó≠Î≥Ñ ÎèÑÏÑúÍ¥Ä Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
  const generateLibrariesByRegion = (region: string): Library[] => {
    const regionLower = region.toLowerCase();
    
    // Îèô Îã®ÏúÑ ÏûÖÎ†• Ï≤òÎ¶¨
    if (regionLower.includes('Îèô')) {
      const cityName = region.replace('Îèô', '');
      return [
        { id: 1, name: `${cityName}Îèô Ï£ºÎØºÏÑºÌÑ∞ ÎèÑÏÑúÍ¥Ä`, address: `Í≤ΩÍ∏∞ÎèÑ ${cityName}Îèô Ï§ëÏïôÎ°ú 123`, phone: '031-123-4567' },
        { id: 2, name: `${cityName}Îèô Íµ¨ÎØºÌöåÍ¥Ä ÎèÑÏÑúÍ¥Ä`, address: `Í≤ΩÍ∏∞ÎèÑ ${cityName}Îèô Î≥µÌï©Î¨∏ÌôîÏÑºÌÑ∞ 2Ï∏µ`, phone: '031-234-5678' },
        { id: 3, name: `${cityName}Îèô ÏûëÏùÄÎèÑÏÑúÍ¥Ä`, address: `Í≤ΩÍ∏∞ÎèÑ ${cityName}Îèô Ï£ºÌÉùÍ∞Ä ÎÇ¥ ÏûëÏùÄÎèÑÏÑúÍ¥Ä`, phone: '031-345-6789' }
      ];
    }
    
    // Í∏∞Ï°¥ ÏßÄÏó≠ Ï≤òÎ¶¨
    if (regionLower.includes('ÏàòÏõê')) {
      return [
        { id: 1, name: 'ÏàòÏõêÏãúÎ¶ΩÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏàòÏõêÏãú ÏòÅÌÜµÍµ¨ Í¥ëÍµêÏÇ∞Î°ú 17', phone: '031-228-4311' },
        { id: 2, name: 'ÏàòÏõêÏòÅÌÜµÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏàòÏõêÏãú ÏòÅÌÜµÍµ¨ ÏÑºÌä∏Îü¥ÌÉÄÏö¥Î°ú 22', phone: '031-228-4350' },
        { id: 3, name: 'ÏàòÏõêÎßùÌè¨ÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏàòÏõêÏãú ÏòÅÌÜµÍµ¨ ÎßùÌè¨Î°ú 37', phone: '031-228-4360' }
      ];
    } else if (regionLower.includes('ÏÑ±ÎÇ®')) {
      return [
        { id: 1, name: 'ÏÑ±ÎÇ®ÏãúÎ¶ΩÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏÑ±ÎÇ®Ïãú ÏàòÏ†ïÍµ¨ ÌÉúÌèâÎ°ú 55', phone: '031-729-4600' },
        { id: 2, name: 'ÏÑ±ÎÇ®Î∂ÑÎãπÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏÑ±ÎÇ®Ïãú Î∂ÑÎãπÍµ¨ Î∂àÏ†ïÎ°ú 90', phone: '031-729-4700' },
        { id: 3, name: 'ÏÑ±ÎÇ®ÏàòÏ†ïÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏÑ±ÎÇ®Ïãú ÏàòÏ†ïÍµ¨ ÏàòÏ†ïÎ°ú 201', phone: '031-729-4800' }
      ];
    } else if (regionLower.includes('ÏïàÏñë')) {
      return [
        { id: 1, name: 'ÏïàÏñëÏãúÎ¶ΩÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏïàÏñëÏãú ÎßåÏïàÍµ¨ Î¨∏ÌôîÎ°ú 53', phone: '031-389-0300' },
        { id: 2, name: 'ÏïàÏñëÌèâÏ¥åÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏïàÏñëÏãú ÎèôÏïàÍµ¨ ÌèâÏ¥åÎåÄÎ°ú 389', phone: '031-389-0400' },
        { id: 3, name: 'ÏïàÏñëÏÑùÏàòÎèÑÏÑúÍ¥Ä', address: 'Í≤ΩÍ∏∞ÎèÑ ÏïàÏñëÏãú ÎßåÏïàÍµ¨ ÏÑùÏàòÎ°ú 12', phone: '031-389-0500' }
      ];
    } else if (regionLower.includes('ÏÑúÏö∏')) {
      return [
        { id: 1, name: 'ÏÑúÏö∏ÏãúÎ¶ΩÏ§ëÏïôÎèÑÏÑúÍ¥Ä', address: 'ÏÑúÏö∏Ïãú ÏÑúÏ¥àÍµ¨ ÎÇ®Î∂ÄÏàúÌôòÎ°ú 2423', phone: '02-2133-0300' },
        { id: 2, name: 'Í∞ïÎÇ®Íµ¨Î¶ΩÎèÑÏÑúÍ¥Ä', address: 'ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨ ÌÖåÌó§ÎûÄÎ°ú 123', phone: '02-2133-0400' },
        { id: 3, name: 'ÎßàÌè¨Íµ¨Î¶ΩÎèÑÏÑúÍ¥Ä', address: 'ÏÑúÏö∏Ïãú ÎßàÌè¨Íµ¨ ÌôçÏùµÎ°ú 45', phone: '02-2133-0500' }
      ];
    } else {
      return [
        { id: 1, name: `${region} Ï§ëÏïôÎèÑÏÑúÍ¥Ä`, address: `${region} Ï§ëÏïôÎ°ú 123`, phone: '031-123-4567' },
        { id: 2, name: `${region} ÏãúÎ¶ΩÎèÑÏÑúÍ¥Ä`, address: `${region} ÏãúÏ≤≠Î°ú 456`, phone: '031-234-5678' },
        { id: 3, name: `${region} Íµ¨Î¶ΩÎèÑÏÑúÍ¥Ä`, address: `${region} Î¨∏ÌôîÎ°ú 789`, phone: '031-345-6789' }
      ];
    }
  };

  // ÎèÑÏÑúÍ¥Ä Í∞ÄÏö©ÏÑ± Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
  const generateLibraryAvailability = (libraryList: Library[]): LibraryAvailability[] => {
    return libraryList.map(lib => ({
      libraryId: lib.id,
      libraryName: lib.name,
      libraryAddress: lib.address,
      libraryPhone: lib.phone,
      available: Math.random() > 0.3,
      waitingList: Math.floor(Math.random() * 5) + 1
    }));
  };

  // Í≤ÄÏÉâ Ïã§Ìñâ
  const performSearch = (query: string) => {
    setLoading(true);
    setHasSearched(true);

    // ÌÇ§ÏõåÎìúÏóê Îî∞Î•∏ Ïã§Ï†ú Ï±Ö Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
    const getBooksByKeyword = (keyword: string) => {
      const keywordLower = keyword.toLowerCase();
      
      if (keywordLower.includes('ÏÜåÏÑ§') || keywordLower.includes('Î¨∏Ìïô')) {
        return [
          { id: 1, title: '82ÎÖÑÏÉù ÍπÄÏßÄÏòÅ', author: 'Ï°∞ÎÇ®Ï£º', publisher: 'ÎØºÏùåÏÇ¨' },
          { id: 2, title: 'Î¥ÑÎÇ†ÏùÄ Í∞ÑÎã§', author: 'ÍπÄÏú†Ï†ï', publisher: 'Î¨∏ÌïôÍ≥ºÏßÄÏÑ±ÏÇ¨' },
          { id: 3, title: 'Ï±ÑÏãùÏ£ºÏùòÏûê', author: 'ÌïúÍ∞ï', publisher: 'Ï∞ΩÎπÑ' }
        ];
      } else if (keywordLower.includes('Ïó≠ÏÇ¨') || keywordLower.includes('ÌïúÍµ≠ÏÇ¨')) {
        return [
          { id: 1, title: 'ÌïúÍµ≠ÏÇ¨ Ìé∏ÏßÄ', author: 'Î∞ïÏùÄÎ¥â', publisher: 'ÏõÖÏßÑÏßÄÏãùÌïòÏö∞Ïä§' },
          { id: 2, title: 'Ï¥ù, Í∑†, Ïá†', author: 'Ïû¨Î†àÎìú Îã§Ïù¥ÏïÑÎ™¨Îìú', publisher: 'Î¨∏ÌïôÏÇ¨ÏÉÅ' },
          { id: 3, title: 'Ï°∞ÏÑ†ÏôïÏ°∞Ïã§Î°ù', author: 'Íµ≠ÏÇ¨Ìé∏Ï∞¨ÏúÑÏõêÌöå', publisher: 'ÌïúÍµ≠ÌïôÏ§ëÏïôÏó∞Íµ¨Ïõê' }
        ];
      } else if (keywordLower.includes('Í≥ºÌïô') || keywordLower.includes('Í∏∞Ïà†')) {
        return [
          { id: 1, title: 'ÏΩîÏä§Î™®Ïä§', author: 'Ïπº ÏÑ∏Ïù¥Í±¥', publisher: 'ÏÇ¨Ïù¥Ïñ∏Ïä§Î∂ÅÏä§' },
          { id: 2, title: 'Ïù¥Í∏∞Ï†Å Ïú†Ï†ÑÏûê', author: 'Î¶¨Ï≤òÎìú ÎèÑÌÇ®Ïä§', publisher: 'ÏùÑÏú†Î¨∏ÌôîÏÇ¨' },
          { id: 3, title: 'ÏÇ¨ÌîºÏóîÏä§', author: 'Ïú†Î∞ú ÌïòÎùºÎ¶¨', publisher: 'ÍπÄÏòÅÏÇ¨' }
        ];
      } else {
        return [
          { id: 1, title: `${query}Ïùò Ïù¥Ìï¥`, author: 'ÍπÄÎØºÏàò', publisher: 'ÌïôÎ¨∏ÏÇ¨' },
          { id: 2, title: `${query} ÏôÑÏ†ÑÏ†ïÎ≥µ`, author: 'Ïù¥ÏòÅÌù¨', publisher: 'ÍµêÏú°Ï∂úÌåê' },
          { id: 3, title: `${query} ÏûÖÎ¨∏ÏÑú`, author: 'Î∞ïÏ≤†Ïàò', publisher: 'ÏßÄÏãùÍ≥µÎ∞©' }
        ];
      }
    };

    setTimeout(() => {
      const searchBooks = getBooksByKeyword(query);
      const currentLibraries = libraries.length > 0 ? libraries : generateLibrariesByRegion(selectedRegion || 'ÏÑúÏö∏');
      
      const results = searchBooks.map(book => ({
        ...book,
        availability: generateLibraryAvailability(currentLibraries)
      }));
      
      setSearchResults(results);
      setLoading(false);
    }, 1000);
  };

  // Í≤ÄÏÉâ Ìèº Ï†úÏ∂ú
  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      performSearch(searchTerm.trim());
    }
  };

  // ÏòàÏïΩ Ï≤òÎ¶¨
  const handleReserve = (bookId: number, libraryId: number, libraryName: string, libraryPhone: string, isAvailable: boolean) => {
    if (isAvailable) {
      const confirmed = window.confirm(`${libraryName}ÏóêÏÑú ÏòàÏïΩÌïòÏãúÍ≤†ÏäµÎãàÍπå?`);
      if (confirmed) {
        alert('ÏòàÏïΩÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
        window.location.href = `tel:${libraryPhone}`;
      }
    } else {
      const confirmed = window.confirm(`${libraryName}Ïóê ÎåÄÍ∏∞Ïã†Ï≤≠ÌïòÏãúÍ≤†ÏäµÎãàÍπå?`);
      if (confirmed) {
        alert('ÎåÄÍ∏∞Ïã†Ï≤≠Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
        window.location.href = `tel:${libraryPhone}`;
      }
    }
  };

  // ÏßÄÏó≠ ÏÑ†ÌÉù Ï≤òÎ¶¨
  useEffect(() => {
    if (selectedRegion) {
      const regionLibraries = generateLibrariesByRegion(selectedRegion);
      setLibraries(regionLibraries);
      
      // Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏûàÏúºÎ©¥ ÏÉàÎ°úÏö¥ ÏßÄÏó≠ ÎèÑÏÑúÍ¥ÄÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
      if (searchResults.length > 0) {
        const updatedResults = searchResults.map(book => ({
          ...book,
          availability: generateLibraryAvailability(regionLibraries)
        }));
        setSearchResults(updatedResults);
      }
    }
  }, [selectedRegion]);

  return (
    <Container>
      <SearchForm onSubmit={handleSearch}>
        <SearchInput
          type="text"
          placeholder="Ï∞æÏúºÏãúÎäî ÎèÑÏÑúÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <SearchButton type="submit" disabled={loading}>
          {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'üîç Í≤ÄÏÉâ'}
        </SearchButton>
      </SearchForm>

      <RegionSelector onLibrariesUpdate={setLibraries} selectedRegion={selectedRegion} onRegionUpdate={setSelectedRegion} />

      {loading && (
        <LoadingDiv>
          üìö ÎèÑÏÑúÎ•º Í≤ÄÏÉâÌïòÍ≥† ÏûàÏäµÎãàÎã§...
        </LoadingDiv>
      )}

      {!loading && hasSearched && searchResults.length === 0 && (
        <EmptyState>
          <div className="emoji">üìñ</div>
          <div className="title">Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§</div>
          <div className="subtitle">Îã§Î•∏ ÌÇ§ÏõåÎìúÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî</div>
        </EmptyState>
      )}

      {!loading && searchResults.length > 0 && (
        <SearchResults>
          {searchResults.map((book) => (
            <BookCard key={book.id}>
              <BookTitle>{book.title}</BookTitle>
              <BookInfo>
                <span>Ï†ÄÏûê: {book.author}</span>
                <span>Ï∂úÌåêÏÇ¨: {book.publisher}</span>
              </BookInfo>
              
              <AvailabilitySection>
                <AvailabilityTitle>
                  üìç {selectedRegion || 'ÏÑúÏö∏'} ÎèÑÏÑúÍ¥Ä ÎåÄÏ∂ú ÌòÑÌô©
                </AvailabilityTitle>
                <LibraryList>
                  {book.availability.map((avail) => (
                    <LibraryItem key={avail.libraryId}>
                      <LibraryInfo>
                        <div className="name">{avail.libraryName}</div>
                        <div className="address">{avail.libraryAddress}</div>
                      </LibraryInfo>
                      <StatusButton
                        className={avail.available ? 'available' : 'unavailable'}
                        onClick={() => handleReserve(book.id, avail.libraryId, avail.libraryName, avail.libraryPhone, avail.available)}
                      >
                        {avail.available ? '‚úì ÏòàÏïΩÌïòÍ∏∞' : `üí§ ÎåÄÍ∏∞Ïã†Ï≤≠ (${avail.waitingList}Î™Ö)`}
                      </StatusButton>
                    </LibraryItem>
                  ))}
                </LibraryList>
              </AvailabilitySection>
            </BookCard>
          ))}
        </SearchResults>
      )}
    </Container>
  );
};

export default BookSearchSection;
