import React, { useState } from 'react';
import styled from 'styled-components';
import { LibraryAPIService } from '../services/LibraryAPI';

const RegionContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 25px;
  background: #f8f9fa;
  border-radius: 12px;
  border: 2px solid #e9ecef;
`;

const RegionTitle = styled.h3`
  font-size: 22px;
  color: #2c3e50;
  margin: 0;
  text-align: center;
  font-weight: 600;
`;

const RegionInputRow = styled.div`
  display: flex;
  gap: 15px;
  align-items: center;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: stretch;
    gap: 12px;

    button {
      width: 100%;
      min-height: 44px;
      display: flex;
      align-items: center;
      justify-content: center;
      text-align: center;
    }
  }
`;

const RegionInput = styled.input`
  flex: 1;
  padding: 16px 18px;
  font-size: 16px;
  border: 2px solid #e9ecef;
  border-radius: 10px;
  transition: all 0.3s ease;
  box-sizing: border-box;

  &:focus {
    outline: none;
    border-color: #4a90e2;
    box-shadow: 0 0 0 3px rgba(74, 144, 226, 0.1);
  }

  &::placeholder {
    color: #999;
    font-size: 15px;
  }

  @media (max-width: 768px) {
    padding: 14px 16px;
    font-size: 15px;
    border-radius: 8px;

    &::placeholder {
      font-size: 14px;
    }
  }

  @media (max-width: 480px) {
    padding: 12px 14px;
    font-size: 15px;

    &::placeholder {
      font-size: 14px;
    }
  }
`;

const RegionButton = styled.button`
  background: var(--primary-color);
  color: white;
  border: none;
  padding: 15px 30px;
  font-size: 18px;
  font-weight: 600;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;
  min-width: 150px;

  &:hover {
    background: #3a7538;
    transform: translateY(-2px);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
  }
`;

const StatusMessage = styled.div`
  text-align: center;
  font-size: 16px;
  padding: 15px;
  border-radius: 8px;
  background: #e3f2fd;
  color: #1976d2;
  font-weight: 500;
`;

const ErrorMessage = styled.div`
  color: #d32f2f;
  font-size: 16px;
  text-align: center;
  padding: 15px;
  background: #ffebee;
  border-radius: 8px;
  border-left: 4px solid #d32f2f;
`;

const GuideText = styled.div`
  text-align: center;
  font-size: 16px;
  color: #666;
  line-height: 1.5;
`;

interface Library {
  id: number;
  name: string;
  address: string;
  distance: number;
  phone: string;
  hours: string;
  status: string;
}

interface RegionSelectorProps {
  onLibrariesUpdate: (libraries: Library[]) => void;
  selectedRegion: string;
  onRegionUpdate: (region: string) => void;
}

const RegionSelector: React.FC<RegionSelectorProps> = ({ 
  onLibrariesUpdate, 
  selectedRegion, 
  onRegionUpdate 
}) => {
  const [regionInput, setRegionInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Í≤ΩÍ∏∞Îç∞Ïù¥ÌÑ∞ÎìúÎ¶º API ÏÑúÎπÑÏä§ Ïù∏Ïä§ÌÑ¥Ïä§ ÏÉùÏÑ±
  const libraryAPI = new LibraryAPIService({
    baseURL: 'https://openapi.gg.go.kr',
    apiKey: process.env.REACT_APP_GYEONGGI_API_KEY || 'demo_key'
  });

  const handleRegionSearch = async () => {
    if (!regionInput.trim()) {
      setError('ÏßÄÏó≠ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Ïã§Ï†ú Í≤ΩÍ∏∞Îç∞Ïù¥ÌÑ∞ÎìúÎ¶º API Ìò∏Ï∂ú
      const apiLibraries = await libraryAPI.getLibraries();

      console.log('APIÏóêÏÑú Î∞õÏùÄ ÎèÑÏÑúÍ¥Ä Îç∞Ïù¥ÌÑ∞:', apiLibraries); // ÎîîÎ≤ÑÍπÖ

      // Ï£ºÏÜå Í∏∞Î∞ò ÌïÑÌÑ∞ÎßÅ - Îçî Ïú†Ïó∞Ìïú Îß§Ïπ≠
      const filteredLibraries = apiLibraries
        .filter(lib => {
          // Ï£ºÏÜåÏóêÏÑú Ïãú/Íµ∞/Íµ¨ Ï∂îÏ∂úÌïòÏó¨ Îß§Ïπ≠
          const addressKeywords = regionInput.split(' ').filter(keyword => keyword.length > 0);
          const searchText = regionInput.toLowerCase();
          const libraryText = (lib.address + ' ' + lib.name).toLowerCase();

          // Îçî Ïú†Ïó∞Ìïú Í≤ÄÏÉâ: Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Îß§Ïπ≠
          return addressKeywords.some(keyword => 
            libraryText.includes(keyword.toLowerCase())
          ) || libraryText.includes(searchText);
        })
        .map((lib, index) => ({
          id: index + 1,
          name: lib.name,
          address: lib.address,
          distance: lib.distance,
          phone: lib.phone,
          hours: lib.hours,
          status: 'Ïö¥ÏòÅÏ§ë'
        }));

      console.log('ÌïÑÌÑ∞ÎßÅÎêú ÎèÑÏÑúÍ¥Ä:', filteredLibraries); // ÎîîÎ≤ÑÍπÖ
      console.log('ÏûÖÎ†•Îêú ÏßÄÏó≠:', regionInput); // ÎîîÎ≤ÑÍπÖ

      // ÌïÑÌÑ∞ÎßÅÎêú Í≤∞Í≥ºÍ∞Ä ÏóÜÍ±∞ÎÇò Ï†ÅÏùÄ Í≤ΩÏö∞ Îçî ÎßéÏùÄ ÎèÑÏÑúÍ¥Ä ÌëúÏãú
      const finalLibraries = filteredLibraries.length > 0 
        ? filteredLibraries 
        : apiLibraries.map((lib, index) => ({
            id: index + 1,
            name: lib.name,
            address: lib.address,
            distance: lib.distance,
            phone: lib.phone,
            hours: lib.hours,
            status: 'Ïö¥ÏòÅÏ§ë'
          }));

      console.log('ÏµúÏ¢Ö ÎèÑÏÑúÍ¥Ä Î™©Î°ù:', finalLibraries); // ÎîîÎ≤ÑÍπÖ

      onLibrariesUpdate(finalLibraries);
      onRegionUpdate(regionInput);

      // ÏÑ±Í≥µ Î©îÏãúÏßÄ ÌëúÏãúÎ•º ÏúÑÌïú Í∞ÑÎã®Ìïú ÏïåÎ¶º
      if (finalLibraries.length > 0) {
        console.log(`‚úÖ ${finalLibraries.length}Í∞úÏùò ÎèÑÏÑúÍ¥ÄÏùÑ Ï∞æÏïòÏäµÎãàÎã§!`);
      }

    } catch (error) {
      console.error('ÎèÑÏÑúÍ¥Ä Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      setError('ÎèÑÏÑúÍ¥Ä Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleRegionSearch();
    }
  };

  return (
    <RegionContainer>
      <RegionTitle>üìç ÏßÄÏó≠ ÏÑ†ÌÉù</RegionTitle>

      <GuideText>
        Í±∞Ï£ºÌïòÏãúÎäî ÏßÄÏó≠ÏùÑ ÏûÖÎ†•ÌïòÏãúÎ©¥ Ìï¥Îãπ ÏßÄÏó≠ ÎèÑÏÑúÍ¥ÄÎì§Ïùò<br/>
        ÎèÑÏÑú ÎåÄÏ∂ú ÌòÑÌô©ÏùÑ ÌôïÏù∏Ìï† Ïàò ÏûàÏäµÎãàÎã§.
        <br/><br/>
        <strong>üí° ÏßÄÏó≠ Í≤ÄÏÉâ ÌåÅ:</strong><br/>
        ‚Ä¢ Ïãú Ïù¥Î¶ÑÎßå ÏûÖÎ†•ÌïòÏÖîÎèÑ Îê©ÎãàÎã§ (Ïòà: ÏàòÏõê, ÏÑ±ÎÇ®, Í≥†Ïñë)<br/>
        ‚Ä¢ Ï†ÑÏ≤¥ ÎèÑÏÑúÍ¥ÄÏùÑ Î≥¥Î†§Î©¥ "Í≤ΩÍ∏∞"Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî
      </GuideText>

      <RegionInputRow>
        <RegionInput
          type="text"
          placeholder="ÏßÄÏó≠Î™Ö ÏûÖÎ†• (Ïòà: ÏàòÏõêÏãú, ÏÑ±ÎÇ®Ïãú, Î∂ÄÏ≤úÏãú)"
          value={regionInput}
          onChange={(e) => setRegionInput(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <RegionButton 
          onClick={handleRegionSearch}
          disabled={loading}
        >
          {loading ? 'Í≤ÄÏÉâÏ§ë...' : 'üîç ÎèÑÏÑúÍ¥Ä Ï∞æÍ∏∞'}
        </RegionButton>
      </RegionInputRow>

      {selectedRegion && (
        <StatusMessage>
          ‚úÖ <strong>{selectedRegion}</strong> ÏßÄÏó≠Ïùò ÎèÑÏÑúÍ¥Ä Ï†ïÎ≥¥Î•º Î∂àÎü¨ÏôîÏäµÎãàÎã§!<br/>
          ÏúÑÏóêÏÑú Í∞Å ÎèÑÏÑúÍ¥ÄÎ≥Ñ ÎåÄÏ∂ú ÌòÑÌô©ÏùÑ ÌôïÏù∏Ìï¥Î≥¥ÏÑ∏Ïöî üìö
        </StatusMessage>
      )}

      {error && (
        <ErrorMessage>
          ‚ö†Ô∏è {error}
        </ErrorMessage>
      )}
    </RegionContainer>
  );
};

export default RegionSelector;
