import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { fetchPopularBooks, PopularBookData, checkApiConfiguration } from '../services/LibraryAPI';

const Container = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const BookTable = styled.div`
  background: white;
  border: 2px solid #e8e8e8;
  border-radius: 12px;
  margin-top: 25px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);

  @media (max-width: 768px) {
    margin-top: 20px;
    border-radius: 10px;
  }

  @media (max-width: 480px) {
    margin-top: 15px;
    border-radius: 8px;
  }
`;

const TableHeader = styled.div`
  display: grid;
  grid-template-columns: 60px 1fr 120px 100px 80px 100px;
  background: var(--primary-color);
  color: white;
  padding: 16px 20px;
  font-weight: 600;
  font-size: 16px;
  align-items: center;
  gap: 12px;

  @media (max-width: 768px) {
    grid-template-columns: 50px 1fr 100px 80px 70px;
    padding: 12px 15px;
    font-size: 14px;
    gap: 8px;

    .desktop-only {
      display: none;
    }
  }

  @media (max-width: 480px) {
    grid-template-columns: 40px 1fr 80px 60px;
    padding: 10px 12px;
    font-size: 13px;
    gap: 6px;

    .tablet-hide {
      display: none;
    }
  }
`;

const HeaderCell = styled.div`
  text-align: center;

  &:nth-child(2) {
    text-align: left;
  }
`;

const BookRow = styled.div`
  display: grid;
  grid-template-columns: 60px 1fr 120px 100px 80px 100px;
  padding: 16px 20px;
  align-items: center;
  gap: 12px;
  border-bottom: 1px solid #f0f0f0;
  transition: background-color 0.2s;
  cursor: pointer;

  &:hover {
    background-color: #f8f9fa;
  }

  &:last-child {
    border-bottom: none;
  }

  @media (max-width: 768px) {
    grid-template-columns: 50px 1fr 100px 80px 70px;
    padding: 12px 15px;
    font-size: 14px;
    gap: 8px;

    .desktop-only {
      display: none;
    }
  }

  @media (max-width: 480px) {
    grid-template-columns: 40px 1fr 80px 60px;
    padding: 10px 12px;
    font-size: 13px;
    gap: 6px;

    .tablet-hide {
      display: none;
    }
  }
`;

const RankCell = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--primary-color);
  color: white;
  border-radius: 50%;
  width: 32px;
  height: 32px;
  font-weight: bold;
  font-size: 14px;

  @media (max-width: 768px) {
    width: 28px;
    height: 28px;
    font-size: 12px;
  }

  @media (max-width: 480px) {
    width: 24px;
    height: 24px;
    font-size: 11px;
  }
`;

const BookInfoCell = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
  min-width: 0;
`;

const BookTitle = styled.div`
  font-weight: 600;
  font-size: 16px;
  color: #2c3e50;
  line-height: 1.3;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;

  @media (max-width: 768px) {
    font-size: 14px;
  }

  @media (max-width: 480px) {
    font-size: 13px;
  }
`;

const BookAuthor = styled.div`
  font-size: 13px;
  color: #7f8c8d;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;

  @media (max-width: 768px) {
    font-size: 12px;
  }

  @media (max-width: 480px) {
    font-size: 11px;
  }
`;

const DataCell = styled.div`
  text-align: center;
  font-size: 14px;
  color: #2c3e50;
  font-weight: 500;

  @media (max-width: 768px) {
    font-size: 13px;
  }

  @media (max-width: 480px) {
    font-size: 12px;
  }
`;

const LoanCountCell = styled(DataCell)`
  font-weight: 600;
  color: var(--primary-color);
`;

const Description = styled.p`
  font-size: 16px;
  color: #7f8c8d;
  text-align: center;
  margin-bottom: 20px;
  line-height: 1.5;
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 40px;
  color: #7f8c8d;
  font-size: 16px;
`;

const ApiStatus = styled.div<{ isError?: boolean }>`
  font-size: 12px;
  color: ${props => props.isError ? '#dc3545' : '#6c757d'};
  text-align: center;
  margin-bottom: 10px;
  padding: 5px 10px;
  background: ${props => props.isError ? '#f8d7da' : '#f8f9fa'};
  border-radius: 4px;
  border: 1px solid ${props => props.isError ? '#f5c6cb' : '#e9ecef'};
`;

const ClickGuide = styled.p`
  font-size: 14px;
  color: var(--primary-color);
  text-align: center;
  margin-bottom: 20px;
  font-weight: 500;
  background: #f0f8f0;
  padding: 10px;
  border-radius: 8px;
  border: 1px solid #e6f3e6;
`;

interface BookSearchSectionProps {
  externalSearchQuery?: string;
  onExternalSearchComplete?: () => void;
  selectedRegion?: string;
  onRegionUpdate?: (region: string) => void;
}

const BookSearchSection: React.FC<BookSearchSectionProps> = ({ 
  externalSearchQuery, 
  onExternalSearchComplete,
  selectedRegion,
  onRegionUpdate 
}) => {
  const [popularBooks, setPopularBooks] = useState<PopularBookData[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [apiEnabled, setApiEnabled] = useState(false);
  const [apiError, setApiError] = useState<string | null>(null);

  const loadPopularBooks = useCallback(async () => {
    try {
      setIsLoading(true);
      setApiError(null);

      const apiConfig = checkApiConfiguration();
      setApiEnabled(apiConfig);

      const books = await fetchPopularBooks();
      setPopularBooks(books.slice(0, 5)); // Top 5만 표시
    } catch (error) {
      console.error('인기 도서 로딩 실패:', error);
      setApiError(error instanceof Error ? error.message : '알 수 없는 오류가 발생했습니다.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadPopularBooks();
  }, [loadPopularBooks]);

  const handleBookClick = (title: string) => {
    console.log('도서 클릭:', title);
    // 여기에 도서 클릭 시 동작을 구현할 수 있습니다
  };

  return (
    <Container>
      <Description>
        📊 지금 가장 많이 빌려지는 도서 TOP 5 (성인 대상)
      </Description>

      <ApiStatus isError={!!apiError}>
        {apiError ? (
          apiError.includes('활성화') ? 
          '⚠️ API 키가 아직 활성화되지 않았습니다. 도서관 정보나루에서 승인을 기다려주세요.' :
          `⚠️ API 오류: ${apiError}`
        ) : 
         apiEnabled ? '🔗 실제 도서관 API 연결 준비됨' : '📋 더미 데이터 사용 중 (API 키 설정 필요)'}
      </ApiStatus>

      <ClickGuide>
        💡 우리 동네 도서관 대출 현황을 한눈에 확인하세요!
      </ClickGuide>

      {isLoading ? (
        <LoadingContainer>
          📚 인기 도서를 불러오는 중...
        </LoadingContainer>
      ) : (
        <BookTable>
          <TableHeader>
            <HeaderCell>순위</HeaderCell>
            <HeaderCell>도서 정보</HeaderCell>
            <HeaderCell className="desktop-only">출판사</HeaderCell>
            <HeaderCell className="tablet-hide">출간년도</HeaderCell>
            <HeaderCell>대출횟수</HeaderCell>
          </TableHeader>

          {popularBooks.map((book, index) => (
            <BookRow key={book.id} onClick={() => handleBookClick(book.title)}>
              <RankCell>{index + 1}</RankCell>
              <BookInfoCell>
                <BookTitle>{book.title}</BookTitle>
                <BookAuthor>{book.author}</BookAuthor>
              </BookInfoCell>
              <DataCell className="desktop-only">{book.publisher}</DataCell>
              <DataCell className="tablet-hide">{book.publishYear}년</DataCell>
              <LoanCountCell>{book.loanCount.toLocaleString()}회</LoanCountCell>
            </BookRow>
          ))}
        </BookTable>
      )}
    </Container>
  );
};

export default BookSearchSection;
