import React, { useState } from 'react';
import styled from 'styled-components';
import { LibraryAPIService } from '../services/LibraryAPI';

const RegionSelectorContainer = styled.div`
  margin-bottom: 20px;
`;

const RegionInputContainer = styled.div`
  display: flex;
  gap: 12px;
  margin-bottom: 16px;
  flex-wrap: wrap;
`;

const RegionInput = styled.input`
  flex: 1;
  min-width: 250px;
  padding: 12px 16px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 16px;
  outline: none;
  transition: border-color 0.3s;

  &:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 175, 80, 0.1);
  }

  &::placeholder {
    color: #666;
  }
`;

const RegionButton = styled.button`
  padding: 12px 24px;
  background: var(--primary-color);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;
  white-space: nowrap;

  &:hover {
    background: #45a049;
    transform: translateY(-2px);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
  }
`;

const StatusMessage = styled.div<{ isError?: boolean }>`
  padding: 12px 16px;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 500;
  margin-bottom: 16px;
  background: ${props => props.isError ? '#fff3cd' : '#d4edda'};
  color: ${props => props.isError ? '#856404' : '#155724'};
  border: 1px solid ${props => props.isError ? '#ffeaa7' : '#c3e6cb'};
`;

const LibraryResults = styled.div`
  background: white;
  border-radius: 8px;
  border: 1px solid #e0e0e0;
  overflow: hidden;
  margin-top: 16px;
`;

const LibraryItem = styled.div`
  padding: 16px;
  border-bottom: 1px solid #f0f0f0;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    background: #f8f9fa;
  }
`;

const LibraryName = styled.h4`
  font-size: 16px;
  font-weight: 600;
  color: #333;
  margin: 0 0 8px 0;
`;

const LibraryInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;

  span {
    font-size: 14px;
    color: #666;

    &.address {
      color: #333;
    }
  }
`;

const QuickRegionButtons = styled.div`
  display: flex;
  gap: 8px;
  margin-bottom: 16px;
  flex-wrap: wrap;
`;

const QuickButton = styled.button`
  padding: 8px 16px;
  background: white;
  color: var(--primary-color);
  border: 2px solid var(--primary-color);
  border-radius: 20px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s;

  &:hover {
    background: var(--primary-color);
    color: white;
  }
`;

const LoadingSpinner = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  font-size: 16px;
  color: #666;
`;

interface RegionSelectorProps {
  onLibrariesUpdate: (libraries: any[]) => void;
  selectedRegion: string;
  onRegionUpdate: (region: string) => void;
}

const RegionSelector: React.FC<RegionSelectorProps> = ({ 
  onLibrariesUpdate, 
  selectedRegion, 
  onRegionUpdate 
}) => {
  const [regionInput, setRegionInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [statusMessage, setStatusMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [libraryResults, setLibraryResults] = useState<any[]>([]);

  // API ÏÑúÎπÑÏä§ Ïù∏Ïä§ÌÑ¥Ïä§ ÏÉùÏÑ±
  const libraryAPI = new LibraryAPIService({
    baseURL: 'https://openapi.gg.go.kr',
    apiKey: process.env.REACT_APP_GYEONGGI_API_KEY || 'demo_key'
  });

  // Ïù∏Í∏∞ ÏßÄÏó≠ Î≤ÑÌäº
  const popularRegions = ['ÏàòÏõêÏãú', 'ÏÑ±ÎÇ®Ïãú', 'Í≥†ÏñëÏãú', 'Î∂ÄÏ≤úÏãú', 'ÏïàÏñëÏãú', 'Ïö©Ïù∏Ïãú'];

  // ÏßÄÏó≠Î≥Ñ ÎçîÎØ∏ ÎèÑÏÑúÍ¥Ä Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
  const generateDummyLibraries = (region: string) => {
    const regionLower = region.toLowerCase();

