  };

  // 즐겨찾기 토글
  const toggleBookmark = (bookId: string) => {
    let updated: string[];
    if (bookmarks.includes(bookId)) {
      updated = bookmarks.filter(id => id !== bookId);
    } else {
      updated = [...bookmarks, bookId];
    }
    setBookmarks(updated);
    localStorage.setItem('bookmarks', JSON.stringify(updated));
  };

  // 즐겨찾기 여부 확인
  const isBookmarked = (bookId: string) => bookmarks.includes(bookId);

  // API 승인 상태 확인
  useEffect(() => {
    const checkApprovalStatus = async () => {
      try {
        const isApproved = await checkApiApprovalStatus('651824a6d5a5d765b513f7f8059ef5ffb2ac3c30f15f0114a8764076c8b902b8');
        if (isApproved) {
          setApiStatus('✅ 실시간 도서관 API 연결됨');
        } else {
          setApiStatus('⚠️ API 승인 대기 중 - 샘플 데이터 제공');
        }
      } catch (error) {
        setApiStatus('⚠️ API 연결 확인 중 - 안정적인 서비스 제공');
      }
    };

    checkApprovalStatus();
  }, []);

  // 지역별 도서관 생성
  const generateLibrariesByRegion = (region: string): Library[] => {
    const regionLower = region.toLowerCase();

    // 동 단위 입력 처리
    if (regionLower.includes('동')) {
      const cityName = region.replace('동', '');
      return [
        { id: 1, name: `${cityName}동 주민센터 도서관`, address: `경기도 ${cityName}동 중앙로 123`, phone: '031-123-4567' },
        { id: 2, name: `${cityName}동 구민회관 도서관`, address: `경기도 ${cityName}동 복합문화센터 2층`, phone: '031-234-5678' },
        { id: 3, name: `${cityName}동 작은도서관`, address: `경기도 ${cityName}동 주택가 내 작은도서관`, phone: '031-345-6789' }
      ];
    }

    // 시/구 단위 입력 처리
    const libraryData: { [key: string]: Library[] } = {
      '수원': [
        { id: 1, name: '수원시립중앙도서관', address: '경기 수원시 영통구 광교로 183', phone: '031-228-4300', hours: '09:00-22:00' },
        { id: 2, name: '경기도립중앙도서관', address: '경기 수원시 영통구 월드컵로 235', phone: '031-249-4800', hours: '09:00-18:00' },
        { id: 3, name: '영통구립도서관', address: '경기 수원시 영통구 영통동 999-1', phone: '031-228-4350', hours: '09:00-20:00' }
      ],
      '성남': [
        { id: 1, name: '성남시립중앙도서관', address: '경기 성남시 분당구 양현로 346', phone: '031-729-4600', hours: '09:00-18:00' },
        { id: 2, name: '분당구립정자도서관', address: '경기 성남시 분당구 정자동 178-1', phone: '031-729-4650', hours: '09:00-20:00' },
        { id: 3, name: '서현작은도서관', address: '경기 성남시 분당구 서현동 271-3', phone: '031-729-4670', hours: '10:00-18:00' }
      ],
      '고양': [
        { id: 1, name: '고양시립중앙도서관', address: '경기 고양시 덕양구 고양대로 1955', phone: '031-8075-9300', hours: '09:00-18:00' },
        { id: 2, name: '일산동구립도서관', address: '경기 고양시 일산동구 백석동 1256', phone: '031-8075-9350', hours: '09:00-20:00' },
        { id: 3, name: '백석작은도서관', address: '경기 고양시 일산동구 백석동 1234-5', phone: '031-8075-9380', hours: '10:00-18:00' }
      ]
    };

    // 지역명에서 키워드 추출
    for (const [key, data] of Object.entries(libraryData)) {
      if (regionLower.includes(key)) {
        return data;
      }
    }

    // 기본 도서관 (서울)
    return [
      { id: 1, name: '서울시립중앙도서관', address: '서울특별시 중구 세종대로 110', phone: '02-2133-0300', hours: '09:00-20:00' },
      { id: 2, name: '강남구립도서관', address: '서울특별시 강남구 테헤란로 114', phone: '02-2182-0300', hours: '09:00-22:00' },
      { id: 3, name: '서초구립도서관', address: '서울특별시 서초구 반포대로 58', phone: '02-2155-8000', hours: '10:00-20:00' }
    ];
  };

  // 도서관별 소장 현황 생성
  const generateLibraryAvailability = (libraries: Library[]) => {
    return libraries.map(library => ({
      libraryId: library.id,
      libraryName: library.name,
      libraryAddress: library.address,
      libraryPhone: library.phone,
      libraryHours: library.hours, // 도서관 운영시간 추가
      available: Math.random() > 0.4, // 60% 확률로 대출 가능
      waitingList: Math.floor(Math.random() * 5) + 1
    }));
  };

  // 검색 수행
  const performSearch = async (query: string) => {
    setLoading(true);
    setHasSearched(true);
    setApiError('');

    try {
      // 실제 API 호출 시도
      const results = await searchBooksAPI(query, 1, 20);

      if (results && results.length > 0) {
        setApiStatus('✅ 실제 도서관 데이터로 검색 완료');

        // 필터 적용
        let filteredResults = results;

        if (filters.category) {
          filteredResults = filteredResults.filter(book => 
            book.category.includes(filters.category)
          );
        }

        if (filters.publishYear) {
          if (filters.publishYear.includes('-')) {
            const [start, end] = filters.publishYear.split('-');
            filteredResults = filteredResults.filter(book => {
              const year = parseInt(book.publishYear);
              return year >= parseInt(start) && year <= parseInt(end);
            });
          } else {
            filteredResults = filteredResults.filter(book => 
              book.publishYear === filters.publishYear
            );
          }
        }

        // 정렬 적용
        if (filters.sortBy === 'newest') {
          filteredResults.sort((a, b) => parseInt(b.publishYear) - parseInt(a.publishYear));
        } else if (filters.sortBy === 'popular') {
          filteredResults.sort((a, b) => (b.loanCount || 0) - (a.loanCount || 0));
        } else if (filters.sortBy === 'title') {
          filteredResults.sort((a, b) => a.title.localeCompare(b.title));
        }

        // 각 도서에 도서관 소장 정보 추가
        const booksWithAvailability = filteredResults.map(book => ({
          ...book,
          availability: generateLibraryAvailability(libraries)
        }));

        // 필터 적용
        let finalResults = booksWithAvailability;

        // 대출 가능한 도서만 필터링
        if (filters.availableOnly) {
          finalResults = finalResults.filter(book => 
            book.availability.some(avail => avail.available)
          );
        }

        // 즐겨찾기 도서만 필터링
        if (filters.bookmarkedOnly) {
          finalResults = finalResults.filter(book => 
            bookmarks.includes(book.id)
          );
        }

        setSearchResults(finalResults);
      } else {
        // API 결과가 없으면 더미 데이터 사용
        setApiStatus('⚠️ 도서관 API 연결 중 - 샘플 데이터 제공');
        const dummyResults = getDummySearchResults(query);
        setSearchResults(dummyResults);
      }
    } catch (error) {
      console.error('검색 중 오류:', error);
      setApiError('API 연결 오류가 발생했습니다. 샘플 데이터를 제공합니다.');
      setApiStatus('⚠️ API 연결 오류 - 안정적인 서비스 제공');

      // 오류 발생 시 더미 데이터 사용
      const dummyResults = getDummySearchResults(query);
      setSearchResults(dummyResults);
    } finally {
      setLoading(false);
    }
  };

  // 더미 검색 결과 생성
  const getDummySearchResults = (query: string): Book[] => {
    const dummyBooks = [
      {
        id: '1',
        title: '82년생 김지영',
        author: '조남주',
        publisher: '민음사',
        isbn: '9788937473722',
        category: '소설',
        publishYear: '2016',
        description: '한국 여성의 현실을 담은 화제의 소설',
        imageUrl: '',
        loanCount: 1247
      },
      {
        id: '2',
        title: '사피엔스',
        author: '유발 하라리',
        publisher: '김영사',
        isbn: '9788934972464',
        category: '인문',
        publishYear: '2015',
        description: '인류의 역사를 다룬 베스트셀러',
        imageUrl: '',
        loanCount: 987
      },
      {
        id: '3',
        title: '아몬드',
        author: '손원평',
        publisher: '창비',
        isbn: '9788936434267',
        category: '소설',
        publishYear: '2017',
        description: '감정을 느끼지 못하는 소년의 성장 소설',
        imageUrl: '',
        loanCount: 876
      }
    ];

    const currentLibraries = libraries.length > 0 ? libraries : generateLibrariesByRegion(selectedRegion || '서울');

    return dummyBooks
      .filter(book => 
        book.title.toLowerCase().includes(query.toLowerCase()) || 
        book.author.toLowerCase().includes(query.toLowerCase())
      )
      .map(book => ({
        ...book,
        availability: generateLibraryAvailability(currentLibraries)
      }));
  };

  // 검색 폼 제출
  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      saveRecentSearch(searchTerm.trim());
      performSearch(searchTerm.trim());
    }
  };

  // 빠른 검색 처리
  const handleQuickSearch = (keyword: string) => {
    setSearchTerm(keyword);
    saveRecentSearch(keyword);
    performSearch(keyword);
  };

  // 필터 변경 처리
  const handleFilterChange = (filterType: string, value: any) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  // 대출 가능 여부 필터 토글
  const toggleAvailableOnly = () => {
    setFilters(prev => ({
      ...prev,
      availableOnly: !prev.availableOnly
    }));
  };

  // 전화 연결 처리
  const handleCall = (libraryName: string, libraryPhone: string, libraryHours?: string) => {
    const currentTime = new Date();
    const currentHour = currentTime.getHours();

    // 도서관 운영시간 확인 (기본 9-18시)
    const isOpen = currentHour >= 9 && currentHour < 18;

    let message = `${libraryName}에 전화하시겠습니까?`;
    if (libraryHours) {
      message += `\n\n📞 전화번호: ${libraryPhone}`;
      message += `\n⏰ 운영시간: ${libraryHours}`;
    }

    if (!isOpen) {
      message += `\n\n⚠️ 현재 시간(${currentHour}시)은 도서관 운영시간 외입니다.`;
    }

    const confirmed = window.confirm(message);
    if (confirmed) {
      window.location.href = `tel:${libraryPhone}`;
    }
  };

  // 예약 처리
  const handleReserve = (bookId: string, libraryId: number, libraryName: string, libraryPhone: string, libraryHours: string = '', isAvailable: boolean) => {
    const currentTime = new Date();
    const currentHour = currentTime.getHours();
    const isOpen = currentHour >= 9 && currentHour < 18;

    let message = '';
    if (isAvailable) {
      message = `${libraryName}에서 이 도서를 예약하시겠습니까?`;
    } else {
      message = `${libraryName}에 대기신청하시겠습니까?`;
    }

    message += `\n\n📞 전화번호: ${libraryPhone}`;
    if (libraryHours) {
      message += `\n⏰ 운영시간: ${libraryHours}`;
    }

    if (!isOpen) {
      message += `\n\n⚠️ 현재 시간은 도서관 운영시간 외입니다.`;
      message += `\n운영시간에 다시 전화해주세요.`;
    }

    const confirmed = window.confirm(message);
    if (confirmed) {
      if (isAvailable) {
        alert('예약 확인을 위해 도서관에 직접 전화드리겠습니다.');
      } else {
        alert('대기신청 확인을 위해 도서관에 직접 전화드리겠습니다.');
      }
      window.location.href = `tel:${libraryPhone}`;
    }
  };

  // 외부 검색어 처리
  useEffect(() => {
    if (externalSearchQuery && externalSearchQuery.trim()) {
      setSearchTerm(externalSearchQuery);
      performSearch(externalSearchQuery);
      if (onExternalSearchComplete) {
        onExternalSearchComplete();
      }
    }
  }, [externalSearchQuery]);

  // 부모에서 전달받은 지역 정보 동기화
  useEffect(() => {
    if (parentSelectedRegion !== undefined) {
      setSelectedRegion(parentSelectedRegion);
    }
  }, [parentSelectedRegion]);

  // 지역 선택 처리
  useEffect(() => {
    if (selectedRegion) {
      const regionLibraries = generateLibrariesByRegion(selectedRegion);
      setLibraries(regionLibraries);

      // 검색 결과가 있으면 새로운 지역 도서관으로 업데이트
      if (searchResults.length > 0) {
        const updatedResults = searchResults.map(book => ({
          ...book,
          availability: generateLibraryAvailability(regionLibraries)
        }));
        setSearchResults(updatedResults);
      }
    }
  }, [selectedRegion]);

  return (
    <Container>
      <SearchForm onSubmit={handleSearch}>
        <SearchInputContainer>
          <SearchInput
            type="text"
            placeholder="찾으시는 도서명을 입력해주세요"
            value={searchTerm}
            onChange={handleSearchInputChange}
            onFocus={handleSearchInputFocus}
            onBlur={handleAutocompleteClose}
          />
          {showAutocomplete && autocompleteItems.length > 0 && (
            <AutocompleteDropdown>
              {autocompleteItems.map((item, index) => (
                <AutocompleteItem key={index} onClick={() => handleAutocompleteSelect(item)}>
                  <span className="icon">
                    {item.type === 'recent' ? '🕐' : item.type === 'popular' ? '🔥' : '💡'}
                  </span>
                  <span className="text">{item.text}</span>
                  <span className="category">{item.category}</span>
                </AutocompleteItem>
              ))}
            </AutocompleteDropdown>
          )}
        </SearchInputContainer>
        <SearchButton type="submit" disabled={loading}>
          {loading ? '검색 중...' : '🔍 검색'}
        </SearchButton>
      </SearchForm>

      <RegionSelector 
        onLibrariesUpdate={setLibraries} 
        selectedRegion={selectedRegion} 
        onRegionUpdate={(region) => {
          setSelectedRegion(region);
          if (onRegionUpdate) {
            onRegionUpdate(region);
          }
        }} 
      />

      <FilterSection>
        <FilterRow>
          <FilterLabel>카테고리:</FilterLabel>
          <FilterSelect value={filters.category} onChange={(e) => handleFilterChange('category', e.target.value)}>
            {categories.map(cat => <option key={cat.value} value={cat.value}>{cat.label}</option>)}
          </FilterSelect>
        </FilterRow>
        <FilterRow>
          <FilterLabel>출판년도:</FilterLabel>
          <FilterSelect value={filters.publishYear} onChange={(e) => handleFilterChange('publishYear', e.target.value)}>
            {publishYears.map(year => <option key={year.value} value={year.value}>{year.label}</option>)}
          </FilterSelect>
        </FilterRow>
        <FilterRow>
          <FilterLabel>정렬:</FilterLabel>
          <FilterSelect value={filters.sortBy} onChange={(e) => handleFilterChange('sortBy', e.target.value)}>
            {sortOptions.map(option => <option key={option.value} value={option.value}>{option.label}</option>)}
          </FilterSelect>
        </FilterRow>
        <FilterRow>
          <FilterToggle active={filters.availableOnly} onClick={toggleAvailableOnly}>
            대출 가능 도서만 보기
          </FilterToggle>
          <FilterToggle active={filters.bookmarkedOnly} onClick={() => setFilters(prev => ({ ...prev, bookmarkedOnly: !prev.bookmarkedOnly }))}>
            즐겨찾기 도서만 보기
          </FilterToggle>
        </FilterRow>
      </FilterSection>

      <QuickSearchSection>
        <QuickSearchTitle>빠른 검색</QuickSearchTitle>
        <QuickSearchTags>
          {quickSearchKeywords.map(keyword => (
            <QuickSearchTag key={keyword} onClick={() => handleQuickSearch(keyword)}>
              {keyword}
            </QuickSearchTag>
          ))}
        </QuickSearchTags>
      </QuickSearchSection>

      {recentSearches.length > 0 && (
        <QuickSearchSection>
          <QuickSearchTitle>최근 검색어</QuickSearchTitle>
          <QuickSearchTags>
            {recentSearches.slice(0, 8).map((search, index) => (
              <QuickSearchTag key={index} onClick={() => handleQuickSearch(search)}>
                🕐 {search}
              </QuickSearchTag>
            ))}
            {recentSearches.length > 0 && (
              <QuickSearchTag 
                onClick={() => {
                  setRecentSearches([]);
                  localStorage.removeItem('recentSearches');
                }}
                style={{ background: '#ffebee', color: '#c62828', border: '1px solid #ffcdd2' }}
              >
                🗑️ 전체 삭제
              </QuickSearchTag>
            )}
          </QuickSearchTags>
        </QuickSearchSection>
      )}

      {apiStatus && (
        <ApiStatus isError={!!apiError}>
          {apiStatus}
        </ApiStatus>
      )}

      {loading && (
        <LoadingDiv>
          📚 실제 도서관 API에서 검색하고 있습니다...
        </LoadingDiv>
      )}

      {!loading && hasSearched && searchResults.length === 0 && (
        <EmptyState>
          <div className="emoji">📖</div>
          <div className="title">검색 결과가 없습니다</div>
          <div className="subtitle">다른 키워드로 검색해보세요</div>
        </EmptyState>
      )}

      {!loading && searchResults.length > 0 && (
        <SearchResults>
          {searchResults.map((book) => (
            <BookCard key={book.id}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '15px' }}>
                <BookTitle>{book.title}</BookTitle>
                <button
                  onClick={() => toggleBookmark(book.id)}
                  style={{
                    background: 'none',
                    border: 'none',
                    fontSize: '24px',
                    cursor: 'pointer',
                    color: isBookmarked(book.id) ? '#ff6b6b' : '#ccc',
                    transition: 'color 0.3s'
                  }}
                  title={isBookmarked(book.id) ? '즐겨찾기 해제' : '즐겨찾기 추가'}
                >
                  {isBookmarked(book.id) ? '❤️' : '🤍'}
                </button>
              </div>
              <BookInfo>
                <span>저자: {book.author}</span>
                <span>출판사: {book.publisher}</span>
                <span>카테고리: {book.category}</span>
                <span>출간년도: {book.publishYear}</span>
                {book.loanCount && <span>대출 횟수: {book.loanCount.toLocaleString()}회</span>}
              </BookInfo>

              {book.description && (
                <BookDescription>
                  {book.description}
                </BookDescription>
              )}

              <AvailabilitySection>
                <AvailabilityTitle>
                  📍 {selectedRegion || '서울'} 도서관 대출 현황
                </AvailabilityTitle>
                <LibraryList>
                  {book.availability
                    .sort((a, b) => {
                      // 대출 가능한 도서관을 우선 표시
                      if (a.available && !b.available) return -1;
                      if (!a.available && b.available) return 1;
                      // 대출 가능한 도서관끼리는 대기자 수 적은 순
                      if (a.available && b.available) return a.waitingList - b.waitingList;
                      // 대출 불가능한 도서관끼리는 대기자 수 적은 순
                      return a.waitingList - b.waitingList;
                    })
                    .map((avail) => (
                    <LibraryItem key={avail.libraryId}>
                      <LibraryInfo>
                        <div className="name">
                          {avail.available ? '✅ ' : '⏳ '}
                          {avail.libraryName}
                        </div>
                        <div className="address">{avail.libraryAddress}</div>
                        <div className="phone">{avail.libraryPhone}</div>
                        {avail.libraryHours && <div className="hours">⏰ {avail.libraryHours}</div>}
                      </LibraryInfo>
                      <ButtonGroup>
                        <StatusButton
                          className={avail.available ? 'available' : 'unavailable'}
                          onClick={() => handleReserve(book.id, avail.libraryId, avail.libraryName, avail.libraryPhone, avail.libraryHours, avail.available)}
                        >
                          {avail.available ? '✓ 바로 예약' : `💤 대기신청 (${avail.waitingList}명)`}
                        </StatusButton>
                        <CallButton onClick={() => handleCall(avail.libraryName, avail.libraryPhone, avail.libraryHours)}>
                          📞 전화하기
                        </CallButton>
                      </ButtonGroup>
                    </LibraryItem>
                  ))}
                </LibraryList>
              </AvailabilitySection>
            </BookCard>
          ))}
        </SearchResults>
      )}
    </Container>
  );
  };

export default BookSearchSection;
