    }));
  };

  // Í≤ÄÏÉâ ÏàòÌñâ
  const performSearch = async (query: string) => {
    setLoading(true);
    setHasSearched(true);
    setApiError('');

    try {
      const results = await searchBooksAPI(query, 1, 20);

      if (results && results.length > 0) {
        setApiStatus('‚úÖ Ïã§Ï†ú ÎèÑÏÑúÍ¥Ä Îç∞Ïù¥ÌÑ∞Î°ú Í≤ÄÏÉâ ÏôÑÎ£å');

        const booksWithAvailability = results.map(book => ({
          ...book,
          availability: generateLibraryAvailability(libraries)
        }));

        setSearchResults(booksWithAvailability);
      } else {
        setApiStatus('‚ö†Ô∏è ÎèÑÏÑúÍ¥Ä API Ïó∞Í≤∞ Ï§ë - ÏÉòÌîå Îç∞Ïù¥ÌÑ∞ Ï†úÍ≥µ');
        const dummyResults = getDummySearchResults(query);
        setSearchResults(dummyResults);
      }
    } catch (error) {
      console.error('Í≤ÄÏÉâ Ï§ë Ïò§Î•ò:', error);
      setApiError('API Ïó∞Í≤∞ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. ÏÉòÌîå Îç∞Ïù¥ÌÑ∞Î•º Ï†úÍ≥µÌï©ÎãàÎã§.');
      setApiStatus('‚ö†Ô∏è API Ïó∞Í≤∞ Ïò§Î•ò - ÏïàÏ†ïÏ†ÅÏù∏ ÏÑúÎπÑÏä§ Ï†úÍ≥µ');

      const dummyResults = getDummySearchResults(query);
      setSearchResults(dummyResults);
    } finally {
      setLoading(false);
    }
  };

  // ÎçîÎØ∏ Í≤ÄÏÉâ Í≤∞Í≥º ÏÉùÏÑ±
  const getDummySearchResults = (query: string): Book[] => {
    const dummyBooks = [
      {
        id: '1',
        title: 'ÎØ∏ÏõÄÎ∞õÏùÑ Ïö©Í∏∞',
        author: 'Í∏∞ÏãúÎØ∏ Ïù¥ÏπòÎ°ú, Í≥†Í∞Ä ÌõÑÎØ∏ÌÉÄÏºÄ',
        publisher: 'Ïù∏ÌîåÎ£®ÏóîÏÖú',
        isbn: '9788950955671',
        category: 'ÏûêÍ∏∞Í≥ÑÎ∞ú',
        publishYear: '2014',
        description: 'ÏïÑÎì§Îü¨ Ïã¨Î¶¨ÌïôÏùÑ Î∞îÌÉïÏúºÎ°ú Ìïú Ïù∏Í∞ÑÍ¥ÄÍ≥ÑÏôÄ ÏÇ∂Ïùò ÏßÄÌòúÎ•º Îã¥ÏùÄ Ï±Ö',
        loanCount: 2847
      },
      {
        id: '2',
        title: '82ÎÖÑÏÉù ÍπÄÏßÄÏòÅ',
        author: 'Ï°∞ÎÇ®Ï£º',
        publisher: 'ÎØºÏùåÏÇ¨',
        isbn: '9788937473371',
        category: 'ÏÜåÏÑ§',
        publishYear: '2016',
        description: 'ÌïúÍµ≠ Ïó¨ÏÑ±Ïùò ÌòÑÏã§ÏùÑ ÏÉùÏÉùÌïòÍ≤å Í∑∏Î¶∞ ÏÜåÏÑ§',
        loanCount: 1923
      },
      {
        id: '3',
        title: 'ÏÇ¨ÌîºÏóîÏä§',
        author: 'Ïú†Î∞ú ÌïòÎùºÎ¶¨',
        publisher: 'ÍπÄÏòÅÏÇ¨',
        isbn: '9788934972464',
        category: 'Ïù∏Î¨∏',
        publishYear: '2015',
        description: 'Ïù∏Î•òÏùò Ïó≠ÏÇ¨Î•º ÌÜµÏ∞∞Ìïú Î≤†Ïä§Ìä∏ÏÖÄÎü¨',
        loanCount: 1456
      }
    ];

    const filtered = dummyBooks.filter(book =>
      book.title.toLowerCase().includes(query.toLowerCase()) ||
      book.author.toLowerCase().includes(query.toLowerCase()) ||
      book.category.toLowerCase().includes(query.toLowerCase())
