  };

  // Í≤ÄÏÉâ Ìèº Ï†úÏ∂ú
  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      saveRecentSearch(searchTerm.trim());
      performSearch(searchTerm.trim());
    }
  };

  // Îπ†Î•∏ Í≤ÄÏÉâ Ï≤òÎ¶¨
  const handleQuickSearch = (keyword: string) => {
    setSearchTerm(keyword);
    saveRecentSearch(keyword);
    performSearch(keyword);
  };

  // Ï†ÑÌôî Ïó∞Í≤∞ Ï≤òÎ¶¨
  const handleCall = (libraryName: string, libraryPhone: string, libraryHours?: string) => {
    const currentTime = new Date();
    const currentHour = currentTime.getHours();

    const isOpen = currentHour >= 9 && currentHour < 18;

    let message = `${libraryName}Ïóê Ï†ÑÌôîÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
    if (libraryHours) {
      message += `\n\nüìû Ï†ÑÌôîÎ≤àÌò∏: ${libraryPhone}`;
      message += `\n‚è∞ Ïö¥ÏòÅÏãúÍ∞Ñ: ${libraryHours}`;
    }

    if (!isOpen) {
      message += `\n\n‚ö†Ô∏è ÌòÑÏû¨ ÏãúÍ∞Ñ(${currentHour}Ïãú)ÏùÄ ÎèÑÏÑúÍ¥Ä Ïö¥ÏòÅÏãúÍ∞Ñ Ïô∏ÏûÖÎãàÎã§.`;
    }

    const confirmed = window.confirm(message);
    if (confirmed) {
      window.location.href = `tel:${libraryPhone}`;
    }
  };

  // ÏòàÏïΩ Ï≤òÎ¶¨
  const handleReserve = (bookId: string, libraryId: number, libraryName: string, libraryPhone: string, libraryHours: string = '', isAvailable: boolean) => {
    const currentTime = new Date();
    const currentHour = currentTime.getHours();
    const isOpen = currentHour >= 9 && currentHour < 18;

    let message = '';
    if (isAvailable) {
      message = `${libraryName}ÏóêÏÑú Ïù¥ ÎèÑÏÑúÎ•º ÏòàÏïΩÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
    } else {
      message = `${libraryName}Ïóê ÎåÄÍ∏∞Ïã†Ï≤≠ÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
    }

    message += `\n\nüìû Ï†ÑÌôîÎ≤àÌò∏: ${libraryPhone}`;
    if (libraryHours) {
      message += `\n‚è∞ Ïö¥ÏòÅÏãúÍ∞Ñ: ${libraryHours}`;
    }

    if (!isOpen) {
      message += `\n\n‚ö†Ô∏è ÌòÑÏû¨ ÏãúÍ∞ÑÏùÄ ÎèÑÏÑúÍ¥Ä Ïö¥ÏòÅÏãúÍ∞Ñ Ïô∏ÏûÖÎãàÎã§.`;
      message += `\nÏö¥ÏòÅÏãúÍ∞ÑÏóê Îã§Ïãú Ï†ÑÌôîÌï¥Ï£ºÏÑ∏Ïöî.`;
    }

    const confirmed = window.confirm(message);
    if (confirmed) {
      if (isAvailable) {
        alert('ÏòàÏïΩ ÌôïÏù∏ÏùÑ ÏúÑÌï¥ ÎèÑÏÑúÍ¥ÄÏóê ÏßÅÏ†ë Ï†ÑÌôîÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.');
      } else {
        alert('ÎåÄÍ∏∞Ïã†Ï≤≠ ÌôïÏù∏ÏùÑ ÏúÑÌï¥ ÎèÑÏÑúÍ¥ÄÏóê ÏßÅÏ†ë Ï†ÑÌôîÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.');
      }
      window.location.href = `tel:${libraryPhone}`;
    }
  };

  // Ïô∏Î∂Ä Í≤ÄÏÉâÏñ¥ Ï≤òÎ¶¨
  useEffect(() => {
    if (externalSearchQuery && externalSearchQuery.trim()) {
      setSearchTerm(externalSearchQuery);
      performSearch(externalSearchQuery);
      if (onExternalSearchComplete) {
        onExternalSearchComplete();
      }
    }
  }, [externalSearchQuery, onExternalSearchComplete]);

  // Î∂ÄÎ™®ÏóêÏÑú Ï†ÑÎã¨Î∞õÏùÄ ÏßÄÏó≠ Ï†ïÎ≥¥ ÎèôÍ∏∞Ìôî
  useEffect(() => {
    if (parentSelectedRegion !== undefined) {
      setSelectedRegion(parentSelectedRegion);
    }
  }, [parentSelectedRegion]);

  // ÏßÄÏó≠ ÏÑ†ÌÉù Ï≤òÎ¶¨
  useEffect(() => {
    if (selectedRegion) {
      const regionLibraries = generateLibrariesByRegion(selectedRegion);
      setLibraries(regionLibraries);

      if (searchResults.length > 0) {
        const updatedResults = searchResults.map(book => ({
          ...book,
          availability: generateLibraryAvailability(regionLibraries)
        }));
        setSearchResults(updatedResults);
      }
    }
  }, [selectedRegion, searchResults]);

  return (
    <Container>
      <SearchForm onSubmit={handleSearch}>
        <SearchInputContainer>
          <SearchInput
            type="text"
            placeholder="Ï∞æÏúºÏãúÎäî ÎèÑÏÑúÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
            value={searchTerm}
            onChange={handleSearchInputChange}
            onFocus={handleSearchInputFocus}
            onBlur={handleAutocompleteClose}
          />
          {showAutocomplete && autocompleteItems.length > 0 && (
            <AutocompleteDropdown>
              {autocompleteItems.map((item, index) => (
                <AutocompleteItem key={index} onClick={() => handleAutocompleteSelect(item)}>
                  <span className="icon">
                    {item.type === 'recent' ? 'üïê' : item.type === 'popular' ? 'üî•' : 'üí°'}
                  </span>
                  <span className="text">{item.text}</span>
                  <span className="category">{item.category}</span>
                </AutocompleteItem>
              ))}
            </AutocompleteDropdown>
          )}
        </SearchInputContainer>
        <SearchButton type="submit" disabled={loading}>
          {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'üîç Í≤ÄÏÉâ'}
        </SearchButton>
      </SearchForm>

      <RegionSelector 
        onLibrariesUpdate={setLibraries} 
        selectedRegion={selectedRegion} 
        onRegionUpdate={(region) => {
          setSelectedRegion(region);
          if (onRegionUpdate) {
            onRegionUpdate(region);
          }
        }} 
      />

      <QuickSearchSection>
        <QuickSearchTitle>Îπ†Î•∏ Í≤ÄÏÉâ</QuickSearchTitle>
        <QuickSearchTags>
          {quickSearchKeywords.map(keyword => (
            <QuickSearchTag key={keyword} onClick={() => handleQuickSearch(keyword)}>
              {keyword}
            </QuickSearchTag>
          ))}
        </QuickSearchTags>
      </QuickSearchSection>

      {recentSearches.length > 0 && (
        <QuickSearchSection>
          <QuickSearchTitle>ÏµúÍ∑º Í≤ÄÏÉâÏñ¥</QuickSearchTitle>
          <QuickSearchTags>
            {recentSearches.slice(0, 8).map((search, index) => (
              <QuickSearchTag key={index} onClick={() => handleQuickSearch(search)}>
