            ))}
            {recentSearches.length > 0 && (
              <QuickSearchTag 
                onClick={() => {
                  setRecentSearches([]);
                  localStorage.removeItem('recentSearches');
                }}
                style={{ background: '#ffebee', color: '#c62828', border: '1px solid #ffcdd2' }}
              >
                üóëÔ∏è Ï†ÑÏ≤¥ ÏÇ≠Ï†ú
              </QuickSearchTag>
            )}
          </QuickSearchTags>
        </QuickSearchSection>
      )}

      {apiStatus && (
        <ApiStatus isError={!!apiError}>
          {apiStatus}
        </ApiStatus>
      )}

      {loading && (
        <LoadingDiv>
          üìö Ïã§Ï†ú ÎèÑÏÑúÍ¥Ä APIÏóêÏÑú Í≤ÄÏÉâÌïòÍ≥† ÏûàÏäµÎãàÎã§...
        </LoadingDiv>
      )}

      {!loading && hasSearched && searchResults.length === 0 && (
        <EmptyState>
          <div className="emoji">üìñ</div>
          <div className="title">Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§</div>
          <div className="subtitle">Îã§Î•∏ ÌÇ§ÏõåÎìúÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî</div>
        </EmptyState>
      )}

      {!loading && searchResults.length > 0 && (
        <SearchResults>
          {searchResults.map((book) => (
            <BookCard key={book.id}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '15px' }}>
                <BookTitle>{book.title}</BookTitle>
                <button
                  onClick={() => toggleBookmark(book.id)}
                  style={{
                    background: 'none',
                    border: 'none',
                    fontSize: '24px',
                    cursor: 'pointer',
                    color: isBookmarked(book.id) ? '#ff6b6b' : '#ccc',
                    transition: 'color 0.3s'
                  }}
                  title={isBookmarked(book.id) ? 'Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú' : 'Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä'}
                >
                  {isBookmarked(book.id) ? '‚ù§Ô∏è' : 'ü§ç'}
                </button>
              </div>
              <BookInfo>
                <span>Ï†ÄÏûê: {book.author}</span>
                <span>Ï∂úÌåêÏÇ¨: {book.publisher}</span>
                <span>Ïπ¥ÌÖåÍ≥†Î¶¨: {book.category}</span>
                <span>Ï∂úÍ∞ÑÎÖÑÎèÑ: {book.publishYear}</span>
                {book.loanCount && <span>ÎåÄÏ∂ú ÌöüÏàò: {book.loanCount.toLocaleString()}Ìöå</span>}
              </BookInfo>

              {book.description && (
                <BookDescription>
                  {book.description}
                </BookDescription>
              )}

              <AvailabilitySection>
                <AvailabilityTitle>
                  üìç {selectedRegion || 'ÏÑúÏö∏'} ÎèÑÏÑúÍ¥Ä ÏÜåÏû• ÌòÑÌô©
                </AvailabilityTitle>
                <LibraryList>
                  {book.availability
                    .sort((a, b) => {
                      if (a.available && !b.available) return -1;
                      if (!a.available && b.available) return 1;
                      if (a.available && b.available) return a.waitingList - b.waitingList;
                      return a.waitingList - b.waitingList;
                    })
                    .map((avail) => (
                    <LibraryItem key={avail.libraryId}>
                      <LibraryInfo>
                        <div className="name">
                          {avail.available ? '‚úÖ ' : '‚è≥ '}
                          {avail.libraryName}
                        </div>
                        <div className="address">{avail.libraryAddress}</div>
                        <div className="inventory">
                          üìö Ï¥ù {avail.totalBooks}Í∂å Î≥¥Ïú† | ÎåÄÏ∂ú Í∞ÄÎä•: {avail.availableBooks}Í∂å
                        </div>
                        <div className="inventory">
                          üìç ÏúÑÏπò: {avail.location}
                        </div>
                        <div className="phone">{avail.libraryPhone}</div>
                        {avail.libraryHours && <div className="hours">‚è∞ {avail.libraryHours}</div>}
                      </LibraryInfo>
                      <ButtonGroup>
                        <StatusButton
                          className={avail.available ? 'available' : 'unavailable'}
                          onClick={() => handleReserve(book.id, avail.libraryId, avail.libraryName, avail.libraryPhone, avail.libraryHours, avail.available)}
                        >
                          {avail.available ? '‚úì Î∞îÎ°ú ÏòàÏïΩ' : `üí§ ÎåÄÍ∏∞Ïã†Ï≤≠ (${avail.waitingList}Î™Ö)`}
                        </StatusButton>
                        <CallButton onClick={() => handleCall(avail.libraryName, avail.libraryPhone, avail.libraryHours)}>
                          üìû Ï†ÑÌôîÌïòÍ∏∞
                        </CallButton>
                      </ButtonGroup>
                    </LibraryItem>
                  ))}
                </LibraryList>
              </AvailabilitySection>
            </BookCard>
          ))}
        </SearchResults>
      )}
    </Container>
  );
};

export default BookSearchSection;
