            libraries={libraries}
            onLibrariesUpdate={setLibraries}
            selectedRegion={selectedRegion}
            onRegionUpdate={setSelectedRegion}
            externalSearchQuery={searchQuery}
            onExternalSearchComplete={handleExternalSearchComplete}
          />
        </div>
        
        <div className="mb-10 bg-white rounded-xl p-6 md:p-8 shadow-lg border border-gray-100">
          <h2 className="text-2xl font-bold text-text mb-6 text-center">üó∫Ô∏è Ïö∞Î¶¨ ÎèôÎÑ§ ÎèÑÏÑúÍ¥Ä ÎåÄÏ∂ú ÌòÑÌô©Ïù¥ ÌïúÎààÏóê!</h2>
