    font-size: 14px;
    color: #7f8c8d;

    &:before {
      content: "‚Ä¢ ";
      color: #4CAF50;
      font-weight: bold;
    }

    &:first-child:before {
      content: "";
    }
  }
`;

const LibraryList = styled.div`
  margin-top: 15px;
  border-top: 1px solid #eee;
  padding-top: 15px;
`;

const LibraryItem = styled.div`
