import React from 'react';

const Header: React.FC = () => {
  return (
    <header className="bg-primary text-white p-6 text-center mb-8 rounded-xl shadow-lg">
      <h1 className="text-[28px] font-bold text-white mb-3">
        북소리
      </h1>
      <p className="text-[16px] leading-relaxed text-white opacity-90 mb-4">
        우리 동네 도서관 책을 한눈에, 함께 읽는 사람도 함께 찾아보세요.
      </p>
      <div className="flex justify-center">
        <button className="bg-accent text-white px-6 py-3 rounded-lg text-[16px] font-semibold hover:bg-opacity-90 transition-all duration-300 shadow-md">
          책 찾기
        </button>
      </div>
    </header>
  );
};

export default Header;
