import React, { useState } from 'react';
import styled from 'styled-components';
import Header from '../components/Header';
import BookSearchSection from '../components/BookSearchSection';
import ReadingGroupSection from '../components/ReadingGroupSection';
import PromotionBanner from '../components/PromotionBanner';
import PopularBooksSection from '../components/PopularBooksSection';

interface Library {
  id: number;
  name: string;
  address: string;
  distance: number;
  phone: string;
  hours: string;
  status: string;
  type?: string;
}

const PageContainer = styled.div<{ 
  fontSize: number; 
  theme: 'normal' | 'dark' | 'high-contrast' 
}>`
  min-height: 100vh;
  font-size: ${props => props.fontSize}%;

  ${props => {
    switch (props.theme) {
      case 'dark':
        return `
          background: #1a1a1a;
          color: #e0e0e0;
        `;
      case 'high-contrast':
        return `
          background: #000000;
          color: #ffffff;
        `;
      default:
        return `
          background: var(--background-color);
        `;
    }
  }}
`;

const ContentContainer = styled.div`
  max-width: 900px;
  margin: 0 auto;
  padding: 0 16px;

  @media (max-width: 768px) {
    padding: 0 12px;
  }
`;

const Section = styled.div`
  background: var(--white);
  border-radius: 8px;
  padding: 16px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid #e5e5e5;
  margin-bottom: 16px;

  @media (max-width: 768px) {
    padding: 12px;
    margin-bottom: 12px;
    border-radius: 6px;
  }
`;

const SectionTitle = styled.h2`
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--text-color);
  margin: 0 0 12px 0;

  @media (max-width: 768px) {
    font-size: 1.1rem;
    margin: 0 0 8px 0;
  }
`;

const MainPage: React.FC = () => {
  const [selectedRegion, setSelectedRegion] = useState<string>('');
  const [searchQuery, setSearchQuery] = useState<string>('');

  const handleBookClick = (title: string) => {
    setSearchQuery(title);
    // ÌéòÏù¥ÏßÄ ÏµúÏÉÅÎã®ÏúºÎ°ú Ïä§ÌÅ¨Î°§
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleExternalSearchComplete = () => {
    setSearchQuery('');
  };

  return (
    <PageContainer fontSize={fontSize} theme={theme}>
      <ContentContainer>
        <Header />

        <Section>
          <SectionTitle>üìö ÏõêÌïòÎäî Ï±ÖÏùÑ Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî!</SectionTitle>
          <BookSearchSection 
            externalSearchQuery={searchQuery}
            onExternalSearchComplete={handleExternalSearchComplete}
            selectedRegion={selectedRegion}
            onRegionUpdate={setSelectedRegion}
          />
        </Section>

        <Section>
          <SectionTitle>üó∫Ô∏è Ïö∞Î¶¨ ÎèôÎÑ§ ÎèÑÏÑúÍ¥Ä ÎåÄÏ∂ú ÌòÑÌô©Ïù¥ ÌïúÎààÏóê!</SectionTitle>
          <PopularBooksSection 
            onBookClick={handleBookClick}
            selectedRegion={selectedRegion}
          />
        </Section>

        <Section>
          <SectionTitle>ü§ù ÏùΩÍ≥† Ïã∂ÏùÄ Ï±ÖÏù¥ ÏûàÎã§Î©¥, Ìï®Íªò ÏùΩÏùÑ Ïù¥ÏõÉÎèÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî</SectionTitle>
          <ReadingGroupSection 
            libraries={[]}
            selectedRegion={selectedRegion}
          />
        </Section>

        <PromotionBanner />

        <AccessibilityControls 
          onFontSizeChange={setFontSize}
          onThemeChange={setTheme}
        />
      </ContentContainer>
    </PageContainer>
  );
};

export default MainPage;
