import React, { useState } from 'react';
import styled from 'styled-components';

const GroupContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 30px;
  
  @media (max-width: 768px) {
    gap: 25px;
  }
  
  @media (max-width: 480px) {
    gap: 20px;
  }
`;

const TabContainer = styled.div`
  display: flex;
  gap: 12px;
  margin-bottom: 25px;
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
  padding-bottom: 8px;
  
  &::-webkit-scrollbar {
    height: 4px;
  }
  
  &::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 4px;
  }
  
  &::-webkit-scrollbar-thumb {
    background: #c1c1c1;
    border-radius: 4px;
  }
  
  @media (max-width: 768px) {
    gap: 8px;
    margin-bottom: 20px;
    flex-wrap: nowrap;
    padding-bottom: 6px;
  }
  
  @media (max-width: 480px) {
    gap: 6px;
    margin-bottom: 15px;
    padding-bottom: 4px;
  }
`;

const TabButton = styled.button<{ active: boolean }>`
  background: ${props => props.active ? 'var(--primary-color)' : '#f8f9fa'};
  color: ${props => props.active ? 'white' : '#555'};
  border: 2px solid ${props => props.active ? 'var(--primary-color)' : '#e9ecef'};
  padding: 15px 25px;
  font-size: 16px;
  font-weight: 600;
  border-radius: 10px;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;
  flex-shrink: 0;
  min-width: fit-content;
  
  &:hover {
    background: ${props => props.active ? '#3a7538' : '#e9ecef'};
    transform: translateY(-1px);
  }
  
  &:focus {
    outline: 3px solid rgba(76, 140, 74, 0.3);
    outline-offset: 2px;
  }
  
  @media (max-width: 768px) {
    padding: 12px 18px;
    font-size: 15px;
    border-radius: 8px;
    min-width: auto;
  }
  
  @media (max-width: 480px) {
    padding: 10px 14px;
    font-size: 14px;
    border-radius: 6px;
  }
`;

const GroupForm = styled.form`
  background: #f8f9fa;
  padding: 25px;
  border-radius: 12px;
  border: 2px solid #e9ecef;
  display: flex;
  flex-direction: column;
  gap: 15px;
  
  @media (max-width: 768px) {
    padding: 20px;
    gap: 12px;
  }
  
  @media (max-width: 480px) {
    padding: 15px;
    gap: 10px;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-bottom: 20px;
  
  @media (max-width: 768px) {
    gap: 8px;
    margin-bottom: 18px;
  }
  
  @media (max-width: 480px) {
    gap: 6px;
    margin-bottom: 15px;
  }
`;

const Label = styled.label`
  font-size: 18px;
  font-weight: 600;
  color: #2c3e50;
  display: flex;
  align-items: center;
  margin-bottom: 8px;
  
  @media (max-width: 768px) {
    font-size: 16px;
  }
  
  @media (max-width: 480px) {
    font-size: 15px;
    flex-wrap: wrap;
  }
`;

const Input = styled.input`
  padding: 16px;
  font-size: 16px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  background: white;
  transition: all 0.3s ease;
  width: 100%;
  box-sizing: border-box;
  
  &:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 140, 74, 0.1);
  }
  
  &::placeholder {
    color: #999;
    font-size: 15px;
  }
  
  @media (max-width: 768px) {
    padding: 14px;
    font-size: 15px;
    
    &::placeholder {
      font-size: 14px;
    }
  }
  
  @media (max-width: 480px) {
    padding: 12px;
    font-size: 15px;
    
    &::placeholder {
      font-size: 14px;
    }
  }
`;

const TextArea = styled.textarea`
  padding: 16px;
  font-size: 16px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  background: white;
  min-height: 120px;
  resize: vertical;
  transition: all 0.3s ease;
  width: 100%;
  box-sizing: border-box;
  line-height: 1.5;
  
  &:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 140, 74, 0.1);
  }
  
  &::placeholder {
    color: #999;
    font-size: 15px;
  }
  
  @media (max-width: 768px) {
    padding: 14px;
    font-size: 15px;
    min-height: 100px;
    
    &::placeholder {
      font-size: 14px;
    }
  }
  
  @media (max-width: 480px) {
    padding: 12px;
    font-size: 15px;
    min-height: 90px;
    
    &::placeholder {
      font-size: 14px;
    }
  }
`;

const Select = styled.select`
  padding: 16px;
  font-size: 16px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  background: white;
  transition: all 0.3s ease;
  width: 100%;
  box-sizing: border-box;
  cursor: pointer;
  
  &:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(76, 140, 74, 0.1);
  }
  
  option {
    padding: 8px;
    font-size: 16px;
  }
  
  @media (max-width: 768px) {
    padding: 14px;
    font-size: 15px;
    
    option {
      font-size: 15px;
    }
  }
  
  @media (max-width: 480px) {
    padding: 12px;
    font-size: 15px;
    
    option {
      font-size: 15px;
    }
  }
`;

const CreateButton = styled.button`
  background: #28a745;
  color: white;
  border: none;
  padding: 18px 40px;
  font-size: 18px;
  font-weight: 600;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  width: 100%;
  
  &:hover {
    background: #218838;
    transform: translateY(-2px);
  }
  
  &:focus {
    outline: 3px solid rgba(40, 167, 69, 0.3);
    outline-offset: 2px;
  }
  
  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
  }
  
  @media (max-width: 768px) {
    padding: 15px 30px;
    font-size: 16px;
