import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { fetchPopularBooks, PopularBookData, checkApiConfiguration } from '../services/LibraryAPI';

const PopularBooksContainer = styled.div`
  width: 100%;
`;

const BookTable = styled.div`
  background: white;
  border: 2px solid #e8e8e8;
  border-radius: 12px;
  margin-top: 25px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  
  @media (max-width: 768px) {
    margin-top: 20px;
    border-radius: 10px;
  }
  
  @media (max-width: 480px) {
    margin-top: 15px;
    border-radius: 8px;
  }
`;

const TableHeader = styled.div`
  display: grid;
  grid-template-columns: 60px 1fr 120px 100px 80px 100px;
  background: var(--primary-color);
  color: white;
  padding: 16px 20px;
  font-weight: 600;
  font-size: 16px;
  align-items: center;
  gap: 12px;
  
  @media (max-width: 768px) {
    grid-template-columns: 50px 1fr 100px 80px 70px;
    padding: 12px 15px;
    font-size: 14px;
    gap: 8px;
    
    .desktop-only {
      display: none;
    }
  }
  
  @media (max-width: 480px) {
    grid-template-columns: 40px 1fr 80px 60px;
    padding: 10px 12px;
    font-size: 13px;
    gap: 6px;
    
    .tablet-hide {
      display: none;
    }
  }
`;

const HeaderCell = styled.div`
  text-align: center;
  
  &:nth-child(2) {
    text-align: left;
  }
`;

const BookRow = styled.div`
  display: grid;
  grid-template-columns: 60px 1fr 120px 100px 80px 100px;
  padding: 16px 20px;
  align-items: center;
  gap: 12px;
  border-bottom: 1px solid #f0f0f0;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #f8fbff;
    border-left: 4px solid var(--primary-color);
  }
  
  &:last-child {
    border-bottom: none;
  }
  
  @media (max-width: 768px) {
    grid-template-columns: 50px 1fr 100px 80px 70px;
    padding: 12px 15px;
    gap: 8px;
    
    .desktop-only {
      display: none;
    }
  }
  
  @media (max-width: 480px) {
    grid-template-columns: 40px 1fr 80px 60px;
    padding: 10px 12px;
    gap: 6px;
    
    .tablet-hide {
      display: none;
    }
  }
`;

const RankCell = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  background: var(--primary-color);
  color: white;
  border-radius: 50%;
  font-weight: 600;
  font-size: 16px;
  margin: 0 auto;
  
  @media (max-width: 768px) {
    width: 28px;
    height: 28px;
    font-size: 14px;
  }
  
  @media (max-width: 480px) {
    width: 24px;
    height: 24px;
    font-size: 12px;
  }
`;

const BookInfoCell = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
  text-align: left;
`;

const BookTitle = styled.div`
  font-size: 16px;
  font-weight: 600;
  color: #2c3e50;
  line-height: 1.3;
  
  @media (max-width: 768px) {
    font-size: 15px;
  }
  
  @media (max-width: 480px) {
    font-size: 14px;
  }
`;

const BookAuthor = styled.div`
  font-size: 14px;
  color: #7f8c8d;
  
  @media (max-width: 768px) {
    font-size: 13px;
  }
  
  @media (max-width: 480px) {
    font-size: 12px;
  }
`;

const DataCell = styled.div`
  text-align: center;
  font-size: 14px;
  color: #2c3e50;
  font-weight: 500;
  
  @media (max-width: 768px) {
    font-size: 13px;
  }
  
  @media (max-width: 480px) {
    font-size: 12px;
  }
`;

const LoanCountCell = styled(DataCell)`
  font-weight: 600;
  color: var(--primary-color);
`;

const Description = styled.p`
  font-size: 16px;
  color: #7f8c8d;
  text-align: center;
  margin-bottom: 20px;
  line-height: 1.5;
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 40px;
  color: #7f8c8d;
  font-size: 16px;
`;

const ApiStatus = styled.div<{ isError?: boolean }>`
  font-size: 12px;
  color: ${props => props.isError ? '#dc3545' : '#6c757d'};
  text-align: center;
  margin-bottom: 10px;
  padding: 5px 10px;
  background: ${props => props.isError ? '#f8d7da' : '#f8f9fa'};
  border-radius: 4px;
  border: 1px solid ${props => props.isError ? '#f5c6cb' : '#e9ecef'};
`;

const ClickGuide = styled.p`
  font-size: 14px;
  color: var(--primary-color);
  text-align: center;
  margin-bottom: 20px;
  font-weight: 500;
  background: #f0f8f0;
  padding: 10px;
  border-radius: 8px;
  border: 1px solid #e6f3e6;
`;

interface PopularBooksSectionProps {
  onBookClick: (title: string) => void;
  selectedRegion?: string;
}

const PopularBooksSection: React.FC<PopularBooksSectionProps> = ({ onBookClick, selectedRegion }) => {
  const [popularBooks, setPopularBooks] = useState<PopularBookData[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [apiEnabled, setApiEnabled] = useState(false);
  const [apiError, setApiError] = useState<string | null>(null);

  const loadPopularBooks = useCallback(async () => {
    try {
      setIsLoading(true);
      setApiError(null);
      
      const apiConfig = checkApiConfiguration();
      setApiEnabled(apiConfig);
      
      // Ïò§Îäò Í∏∞Ï§Ä ÏµúÏã† Îç∞Ïù¥ÌÑ∞ (2025ÎÖÑ ÏµúÏã† Í∏∞Í∞Ñ)
      const today = new Date();
      const endDate = '2025-07-13'; // ÏÑ§Ï†ïÎêú ÏµúÏã† ÎÇ†Ïßú
      const startDate = '2025-01-01'; // 2025ÎÖÑ ÏãúÏûë
      
      console.log(`üìÖ Ïò§Îäò(${today.toLocaleDateString()}) Í∏∞Ï§Ä ÏµúÏã† Ïù∏Í∏∞ÎèÑÏÑú Ï°∞Ìöå: ${startDate} ~ ${endDate}`);
      
      const books = await fetchPopularBooks(startDate, endDate);
      setPopularBooks(books.slice(0, 5)); // Top 5Îßå ÌëúÏãú
    } catch (error) {
      console.error('Ïù∏Í∏∞ ÎèÑÏÑú Î°úÎî© Ïã§Ìå®:', error);
      setApiError(error instanceof Error ? error.message : 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadPopularBooks();
  }, [loadPopularBooks]);

  return (
    <PopularBooksContainer>
      <Description>
        üìä ÏßÄÍ∏à Í∞ÄÏû• ÎßéÏù¥ ÎπåÎ†§ÏßÄÎäî ÎèÑÏÑú TOP 5 (ÏïÑÎèô ÎèÑÏÑú Ï†úÏô∏)
      </Description>
      
      <ApiStatus isError={!!apiError}>
        {apiError ? (
          apiError.includes('ÌôúÏÑ±Ìôî') ? 
          '‚ö†Ô∏è API ÌÇ§Í∞Ä ÏïÑÏßÅ ÌôúÏÑ±ÌôîÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§. ÎèÑÏÑúÍ¥Ä Ï†ïÎ≥¥ÎÇòÎ£®ÏóêÏÑú ÏäπÏù∏ÏùÑ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.' :
          `‚ö†Ô∏è API Ïò§Î•ò: ${apiError}`
        ) : 
         apiEnabled ? 'üîó Ïã§Ï†ú ÎèÑÏÑúÍ¥Ä API Ïó∞Í≤∞ Ï§ÄÎπÑÎê®' : 'üìã ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö© Ï§ë (API ÌÇ§ ÏÑ§Ï†ï ÌïÑÏöî)'}
      </ApiStatus>
      
      <ClickGuide>
        üí° Ï±Ö Ï†úÎ™©ÏùÑ ÌÅ¥Î¶≠ÌïòÎ©¥ Î∞îÎ°ú Í≤ÄÏÉâÌï† Ïàò ÏûàÏñ¥Ïöî!
      </ClickGuide>
      
